import React, { useState, useContext, useEffect } from 'react';
import {
  Box,
  Fab,
  Paper,
  Button,
  Typography,
  Collapse,
  Divider
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ContrastIcon from '@mui/icons-material/Tonality';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import PsychologyIcon from '@mui/icons-material/Psychology';
import ImageIcon from '@mui/icons-material/Image';
import VisibilityIcon from '@mui/icons-material/Visibility';
import AnimationIcon from '@mui/icons-material/MotionPhotosOff';
import LinkIcon from '@mui/icons-material/Link';
import MouseIcon from '@mui/icons-material/Mouse';
import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [sobreBanner, setSobreBanner] = useState(true);

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto
    // Placeholder: agregar más funciones aquí si las tienes en tu context
  } = useContext(AccessibilityContext);

  useEffect(() => {
    const handleScroll = () => {
      setSobreBanner(window.scrollY < 600);
    };
    window.addEventListener('scroll', handleScroll);
    handleScroll();
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const estiloBoton = sobreBanner
    ? {
        background: 'linear-gradient(135deg, #1976d2, #7e57c2)',
        color: '#fff',
        boxShadow: '0 8px 24px rgba(0,0,0,0.4)',
        width: 64,
        height: 64,
        borderRadius: '50%',
        '&:hover': {
          transform: 'scale(1.1)',
          boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
        },
        transition: 'all 0.3s ease-in-out'
      }
    : {
        backgroundColor: '#ffffffee',
        color: '#1976d2',
        border: '2px solid #1976d2',
        boxShadow: '0 8px 24px rgba(0,0,0,0.35)',
        width: 64,
        height: 64,
        borderRadius: '16px',
        backdropFilter: 'blur(6px)',
        '&:hover': {
          backgroundColor: '#ffffff',
          transform: 'scale(1.1)',
          boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
        },
        transition: 'all 0.3s ease-in-out'
      };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: 24,
        right: 24,
        zIndex: 1300,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-end'
      }}
    >
      <Collapse in={open}>
        <Paper
          elevation={10}
          sx={{
            mb: 2,
            p: 2.5,
            minWidth: 280,
            maxWidth: 320,
            borderRadius: '18px',
            backgroundColor: '#fdfdfd',
            boxShadow: '0 8px 24px rgba(0,0,0,0.2)',
            border: '1px solid #e0e0e0'
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 2 }}>
            ⚙️ Accesibilidad
          </Typography>

          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1.5 }}>
            <Button
              variant="contained"
              startIcon={<ContrastIcon />}
              color={altoContraste ? 'secondary' : 'primary'}
              fullWidth
              onClick={toggleContraste}
            >
              {altoContraste ? 'Desactivar contraste alto' : 'Activar contraste alto'}
            </Button>

            <Button
              variant="contained"
              startIcon={<PsychologyIcon />}
              color={modoDislexia ? 'secondary' : 'primary'}
              fullWidth
              onClick={toggleDislexia}
            >
              {modoDislexia ? 'Desactivar dislexia' : 'Activar dislexia'}
            </Button>

            <Divider />

            <Box>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>
                Escala de texto: <strong>{Math.round(tamañoTexto * 100)}%</strong>
              </Typography>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Button onClick={disminuirTexto} variant="outlined" size="small" sx={{ minWidth: 40 }}>
                  –
                </Button>
                <Button onClick={aumentarTexto} variant="outlined" size="small" sx={{ minWidth: 40 }}>
                  +
                </Button>
              </Box>
            </Box>

            <Divider />

            {/* 💡 Nuevas opciones visuales */}
            <Button variant="outlined" startIcon={<ImageIcon />} fullWidth>
              Ocultar imágenes
            </Button>

            <Button variant="outlined" startIcon={<LinkIcon />} fullWidth>
              Resaltar enlaces
            </Button>

            <Button variant="outlined" startIcon={<AnimationIcon />} fullWidth>
              Detener animaciones
            </Button>

            <Button variant="outlined" startIcon={<MouseIcon />} fullWidth>
              Cursor ampliado
            </Button>
          </Box>
        </Paper>
      </Collapse>

      <Fab onClick={() => setOpen((prev) => !prev)} aria-label="Accesibilidad" sx={estiloBoton}>
        <AccessibilityNewIcon fontSize="large" />
      </Fab>
    </Box>
  );
}
