import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  TextField,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddBoxIcon from '@mui/icons-material/AddBox';
import EditIcon from '@mui/icons-material/Edit';
import LoginModal from '../../components/modals/LoginModal';

export default function Comprar() {
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('token'));
  const rol = localStorage.getItem('rol')?.trim()?.toLowerCase();
  const esAdmin = rol === 'admin';

  const [productos, setProductos] = useState([]);
  const [showLogin, setShowLogin] = useState(false);
  const [carrito, setCarrito] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('todos');

  useEffect(() => {
    fetch('http://localhost:5000/api/productos')
      .then(res => res.json())
      .then(data => setProductos(data))
      .catch(err => console.error('Error al cargar productos:', err));
  }, []);

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      setShowLogin(true);
    } else {
      const seleccionado = productos.find(p => p._id === id);
      if (seleccionado) {
        setCarrito(prev => [...prev, seleccionado]);
      }
    }
  };

  const total = carrito.reduce((acc, prod) => acc + (prod.precio || 0), 0);

  const renderProductos = (lista) => (
    <Grid container spacing={3}>
      {lista.map(prod => (
        <Grid item xs={12} sm={6} md={3} key={prod._id}>
          <Paper
            elevation={3}
            sx={{
              p: 2,
              borderRadius: 3,
              position: 'relative',
              height: '100%',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              transition: 'all 0.3s',
              '&:hover': {
                boxShadow: '0 8px 30px rgba(0,0,0,0.1)',
                transform: 'translateY(-4px)'
              }
            }}
          >
            {prod.descuento && (
              <Box sx={{
                position: 'absolute',
                top: 12,
                right: 12,
                bgcolor: '#f44336',
                color: '#fff',
                px: 1.5,
                py: 0.5,
                borderRadius: 1,
                fontSize: '0.75rem'
              }}>
                -{prod.descuento}%
              </Box>
            )}

            <Box
              component="img"
              src={prod.imagen}
              alt={prod.nombre}
              sx={{
                width: '100%',
                height: 140,
                objectFit: 'cover',
                borderRadius: 2,
                mb: 1
              }}
            />

            <Box sx={{ flexGrow: 1 }}>
              <Typography variant="subtitle1" sx={{ fontWeight: 'bold', color: '#388e3c', mb: 0.5 }}>
                {prod.nombre}
              </Typography>

              <Typography variant="body2" sx={{ color: '#666', mb: 0.5 }}>
                {prod.categoria}
              </Typography>

              <Box sx={{ display: 'flex', alignItems: 'baseline', gap: 1 }}>
                <Typography sx={{ color: '#d32f2f', fontWeight: 'bold' }}>
                  {typeof prod.precio === 'number' ? `$${prod.precio.toFixed(2)}` : 'Precio no disponible'}
                </Typography>
                {typeof prod.precioOriginal === 'number' && (
                  <Typography sx={{ color: '#999', textDecoration: 'line-through' }}>
                    ${prod.precioOriginal.toFixed(2)}
                  </Typography>
                )}
              </Box>

              <Typography variant="caption" sx={{ color: '#999', mt: 0.5 }}>
                (Exento de IVA)
              </Typography>
            </Box>

            <Button
              fullWidth
              onClick={() => handleCompra(prod._id)}
              sx={{
                mt: 2,
                border: '1px solid #fb8c00',
                color: '#fb8c00',
                textTransform: 'none',
                borderRadius: 2,
                fontWeight: 'bold'
              }}
            >
              Agregar
            </Button>

            {esAdmin && (
              <Box sx={{ mt: 1, display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Button
                  fullWidth
                  variant="outlined"
                  startIcon={<EditIcon />}
                  onClick={() => navigate(`/productos/editar/${prod._id}`)}
                  sx={{
                    fontWeight: 'bold',
                    color: '#1976d2',
                    borderColor: '#1976d2',
                    textTransform: 'none',
                    '&:hover': { bgcolor: '#e3f2fd' }
                  }}
                >
                  Editar
                </Button>

                <Button
                  fullWidth
                  variant="outlined"
                  onClick={() => navigate(`/productos/eliminar/${prod._id}`)}
                  sx={{
                    fontWeight: 'bold',
                    color: '#d32f2f',
                    borderColor: '#d32f2f',
                    textTransform: 'none',
                    '&:hover': { bgcolor: '#ffebee' }
                  }}
                >
                  üóëÔ∏è Eliminar
                </Button>
              </Box>
            )}
          </Paper>
        </Grid>
      ))}
    </Grid>
  );

  const filtrarProductos = () => {
    if (categoriaSeleccionada === 'todos') return productos;
    if (categoriaSeleccionada === 'mas-vendidos') return productos.filter(p => p.ventas >= 50);
    return productos.filter(p =>
      p.categoria?.trim().toLowerCase() === categoriaSeleccionada
    );
  };

  return (
    <Box sx={{ pt: 12, pb: 6, px: { xs: 2, md: 4 } }}>
      {/* Header */}
      <Box sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bgcolor: 'white',
        borderBottom: '1px solid #ddd',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        px: 3,
        py: 1,
        zIndex: 1000
      }}>
        <Typography
          variant="h6"
          component="a"
          href="/"
          sx={{
            color: '#2e7d32',
            fontWeight: 'bold',
            textDecoration: 'none',
            fontSize: '1.3rem'
          }}
        >
          Mercado Agr√≠cola
        </Typography>

        <TextField
          size="small"
          placeholder="Buscar producto"
          sx={{
            bgcolor: '#f1f8e9',
            borderRadius: 20,
            width: { xs: 140, md: 240 }
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon sx={{ color: 'gray' }} />
              </InputAdornment>
            )
          }}
        />

        {isLoggedIn ? (
          <Button
            variant="outlined"
            onClick={() => {
              localStorage.removeItem('token');
              localStorage.removeItem('rol');
              window.location.reload();
            }}
            sx={{
              color: '#d32f2f',
              borderColor: '#d32f2f',
              fontWeight: 'bold',
              fontSize: '0.75rem'
            }}
          >
            Cerrar sesi√≥n
          </Button>
        ) : (
          <Button
            variant="outlined"
            onClick={() => setShowLogin(true)}
            sx={{
              color: '#2e7d32',
              borderColor: '#2e7d32',
              fontWeight: 'bold',
              fontSize: '0.75rem'
            }}
          >
            Iniciar sesi√≥n
          </Button>
        )}

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Typography sx={{ fontSize: '1.2rem' }}>üõí</Typography>
          <Typography sx={{ fontWeight: 'bold', fontSize: '0.9rem' }}>
            {carrito.length} | ${total.toFixed(2)}
          </Typography>
        </Box>
      </Box>

      {/* Botones de filtro */}
      <Box sx={{ my: 5, display: 'flex', flexWrap: 'wrap', gap: 2, justifyContent: 'center' }}>
        <Button onClick={() => setCategoriaSeleccionada('mas-vendidos')} variant="outlined" sx={buttonStyle}>
          M√°s vendidos
        </Button>
        <Button onClick={() => setCategoriaSeleccionada('banana')} variant="outlined" sx={buttonStyle}>
          Banana
        </Button>
        <Button onClick={() => setCategoriaSeleccionada('acuacultura')} variant="outlined" sx={buttonStyle}>
          Acuacultura
        </Button>
        <Button onClick={() => setCategoriaSeleccionada('alimento perro')} variant="outlined" sx={buttonStyle}>
          Perros
        </Button>
        <Button onClick={() => setCategoriaSeleccionada('alimento gato')} variant="outlined" sx={buttonStyle}>
          Gatos
        </Button>
        <Button onClick={() => setCategoriaSeleccionada('todos')} variant="outlined" sx={buttonStyle}>
          Todo el cat√°logo
        </Button>
      </Box>

      {/* Bot√≥n de admin */}
      {esAdmin && (
        <Box sx={{ mb: 4, textAlign: 'center' }}>
          <Button
            variant="contained"
            onClick={() => navigate('/productos/nuevo')}
            startIcon={<AddBoxIcon />}
            sx={{
              bgcolor: '#388e3c',
              fontWeight: 'bold',
              px: 4,
              py: 1.5,
              borderRadius: 2,
              '&:hover': { bgcolor: '#2e7d32' }
            }}
          >
            Registrar producto
          </Button>
        </Box>
      )}

      {/* Render din√°mico */}
      <Typography variant="h5" sx={{ fontWeight: 'bold', mt: 4, mb: 3, textAlign: 'center' }}>
        {categoriaSeleccionada === 'mas-vendidos'
          ? 'üí∏ Productos m√°s vendidos'
          : categoriaSeleccionada === 'todos'
          ? 'üåø Todo el cat√°logo'
          : `üì¶ Categor√≠a: ${categoriaSeleccionada.toUpperCase()}`}
      </Typography>

      {renderProductos(filtrarProductos())}

      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
    </Box>
  );
}

const buttonStyle = {
  textTransform: 'none',
  borderRadius: 20,
  px: 3,
  py: 1,
  fontWeight: 'bold',
  color: '#2e7d32',
  borderColor: '#2e7d32',
  '&:hover': { bgcolor: '#e8f5e9' }
};
