import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  MenuItem
} from '@mui/material';

const tiposServicio = [
  'Cotizaci√≥n agr√≠cola',
  'Consulta veterinaria',
  'Laboratorio t√©cnico',
  'Soporte rural',
  'Rastreo de pedido'
];

export default function FormularioServicio() {
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    tipo: '',
    mensaje: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Solicitud enviada:', formData);
    alert('‚úÖ Solicitud enviada correctamente (modo local)');
  };

  return (
    <Box sx={{ pt: 10, px: { xs: 2, md: 4 }, pb: 6 }}>
      <Typography variant="h4" sx={{ fontWeight: 'bold', textAlign: 'center', color: '#2e7d32', mb: 4 }}>
        ‚úçÔ∏è Solicitud de Servicio en L√≠nea
      </Typography>

      <Paper elevation={3} sx={{ p: 4, borderRadius: 3, maxWidth: 600, mx: 'auto' }}>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Nombre completo"
            name="nombre"
            fullWidth
            required
            value={formData.nombre}
            onChange={handleChange}
            sx={{ mb: 3 }}
          />
          <TextField
            label="Correo electr√≥nico"
            name="email"
            type="email"
            fullWidth
            required
            value={formData.email}
            onChange={handleChange}
            sx={{ mb: 3 }}
          />
          <TextField
            label="Tipo de servicio"
            name="tipo"
            select
            fullWidth
            required
            value={formData.tipo}
            onChange={handleChange}
            sx={{ mb: 3 }}
          >
            {tiposServicio.map((tipo, idx) => (
              <MenuItem key={idx} value={tipo}>
                {tipo}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            label="Mensaje o detalle del servicio"
            name="mensaje"
            multiline
            rows={4}
            fullWidth
            required
            value={formData.mensaje}
            onChange={handleChange}
            sx={{ mb: 3 }}
          />
          <Button
            variant="contained"
            type="submit"
            fullWidth
            sx={{ bgcolor: '#388e3c', textTransform: 'none', fontWeight: 'bold' }}
          >
            üì§ Enviar solicitud
          </Button>
        </form>
      </Paper>
    </Box>
  );
}
