import { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { TextField, MenuItem, Button, Typography, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const sectores = ['Costa', 'Andes', 'Amazon√≠a'];
const edades = ['Menor de 18', '18-30', 'Mayor de 30'];
const cursos = {
  Costa: ['Riego eficiente', 'Semillas tropicales'],
  Andes: ['Cultivo en altura', 'Conservaci√≥n de suelo'],
  Amazon√≠a: ['Agroforester√≠a', 'Manejo sostenible'],
};

export default function CapacitacionForm() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [sector, setSector] = useState('');
  const [edad, setEdad] = useState('');
  const [curso, setCurso] = useState('');
  const [feedback, setFeedback] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!sector || !edad || !curso) {
      setFeedback('Todos los campos son obligatorios ‚ùå');
      return;
    }

    if (!user?.token) {
      setFeedback('Debes iniciar sesi√≥n para inscribirte üîê');
      setTimeout(() => navigate('/login'), 1500);
      return;
    }

    const payload = { sector, edad, curso };

    try {
      const res = await fetch('/api/capacitacion', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      setFeedback(data.message || 'Inscripci√≥n registrada correctamente ‚úîÔ∏è');
    } catch (err) {
      console.error('Error al inscribirse:', err);
      setFeedback('Error en el registro ‚ùå');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ maxWidth: 500, mx: 'auto' }}>
      <Typography variant="h5" gutterBottom>Formulario de Capacitaci√≥n</Typography>

      <TextField select label="Sector" value={sector} onChange={(e) => setSector(e.target.value)} fullWidth margin="normal">
        {sectores.map((s) => <MenuItem key={s} value={s}>{s}</MenuItem>)}
      </TextField>

      <TextField select label="Edad" value={edad} onChange={(e) => setEdad(e.target.value)} fullWidth margin="normal">
        {edades.map((e) => <MenuItem key={e} value={e}>{e}</MenuItem>)}
      </TextField>

      {(sector && edad) && (
        <TextField select label="Curso disponible" value={curso} onChange={(e) => setCurso(e.target.value)} fullWidth margin="normal">
          {cursos[sector].map((c) => <MenuItem key={c} value={c}>{c}</MenuItem>)}
        </TextField>
      )}

      <Button type="submit" variant="contained" fullWidth sx={{ mt: 2 }}>Inscribirse</Button>
      {feedback && (
        <Typography mt={2} color={feedback.includes('‚úîÔ∏è') ? 'success.main' : 'error.main'}>
          {feedback}
        </Typography>
      )}
    </Box>
  );
}
