// ✅ AccessibilityContext.jsx COMPLETO
import React, { createContext, useState, useMemo, useEffect, useContext } from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';

export const AccessibilityContext = createContext();

export const AccessibilityProvider = ({ children }) => {
  const [altoContraste, setAltoContraste] = useState(false);
  const [invertirColores, setInvertirColores] = useState(false);
  const [modoDislexia, setModoDislexia] = useState(false);
  const [tamañoTexto, setTamañoTexto] = useState(1);
  const [espaciadoNivel, setEspaciadoNivel] = useState('normal');
  const [alturaLinea, setAlturaLinea] = useState('normal');
  const [ocultarImágenes, setOcultarImágenes] = useState(false);
  const [resaltarEnlaces, setResaltarEnlaces] = useState(false);
  const [detenerAnimaciones, setDetenerAnimaciones] = useState(false);
  const [modoLupa, setModoLupa] = useState(false);
  const [lectorPantalla, setLectorPantalla] = useState(false);
  const [descripcionAudio, setDescripcionAudio] = useState(false);
  const [subtitulosCerrados, setSubtitulosCerrados] = useState(false);
  const [subtitulosEnVivo, setSubtitulosEnVivo] = useState(false);
  const [transcripcion, setTranscripcion] = useState(false);
  const [controlesAudio, setControlesAudio] = useState(false);
  const [alertasVisuales, setAlertasVisuales] = useState(false);
  const [skipLinkActivo, setSkipLinkActivo] = useState(false);

  const aumentarTexto = () => setTamañoTexto((prev) => Math.min(prev + 0.1, 1.6));
  const disminuirTexto = () => setTamañoTexto((prev) => Math.max(prev - 0.1, 0.7));
  const toggleContraste = () => setAltoContraste((prev) => !prev);
  const toggleDislexia = () => setModoDislexia((prev) => !prev);
  const toggleOcultarImágenes = () => setOcultarImágenes((prev) => !prev);
  const toggleResaltarEnlaces = () => setResaltarEnlaces((prev) => !prev);
  const toggleAnimaciones = () => setDetenerAnimaciones((prev) => !prev);
  const toggleModoLupa = () => setModoLupa((prev) => !prev);
  const toggleInvertirColores = () => setInvertirColores((prev) => !prev);

  useEffect(() => {
    document.body.style.animation = detenerAnimaciones ? 'none' : '';
    document.body.classList.toggle('resaltar-enlaces', resaltarEnlaces);
    document.body.classList.toggle('ocultar-imagenes', ocultarImágenes);
    document.body.classList.toggle('invertir-colores', invertirColores);
    document.body.classList.toggle('modo-dislexia', modoDislexia);
    document.body.style.letterSpacing =
      espaciadoNivel === 'medio' ? '0.06em' : espaciadoNivel === 'alto' ? '0.12em' : 'normal';
    document.body.style.lineHeight =
      alturaLinea === 'medio' ? '2' : alturaLinea === 'alto' ? '2.4' : '1.5';
  }, [detenerAnimaciones, resaltarEnlaces, ocultarImágenes, invertirColores, modoDislexia, espaciadoNivel, alturaLinea]);

  useEffect(() => {
    let lupa;
    if (modoLupa) {
      lupa = document.createElement('div');
      lupa.id = 'lupa-visual';
      Object.assign(lupa.style, {
        position: 'fixed',
        pointerEvents: 'none',
        width: '150px',
        height: '150px',
        overflow: 'hidden',
        borderRadius: '50%',
        border: '2px solid #1976d2',
        boxShadow: '0 0 10px #1976d2',
        zIndex: '9999'
      });
      document.body.appendChild(lupa);

      const zoomer = document.createElement('div');
      Object.assign(zoomer.style, {
        transform: 'scale(2)',
        transformOrigin: 'top left',
        width: '100vw',
        height: '100vh',
        position: 'absolute',
        top: 0,
        left: 0
      });
      zoomer.innerHTML = document.body.innerHTML;
      lupa.appendChild(zoomer);

      const mover = (e) => {
        lupa.style.top = `${e.clientY - 75}px`;
        lupa.style.left = `${e.clientX - 75}px`;
        zoomer.style.left = `-${e.clientX * 2 - 75}px`;
        zoomer.style.top = `-${e.clientY * 2 - 75}px`;
      };
      window.addEventListener('mousemove', mover);
      return () => {
        window.removeEventListener('mousemove', mover);
        lupa.remove();
      };
    }
  }, [modoLupa]);

  const theme = useMemo(
    () =>
      createTheme({
        typography: {
          htmlFontSize: 16,
          fontSize: 16 * tamañoTexto
        }
      }),
    [tamañoTexto]
  );

  return (
    <AccessibilityContext.Provider
      value={{
        altoContraste,
        invertirColores,
        modoDislexia,
        tamañoTexto,
        espaciadoNivel,
        alturaLinea,
        ocultarImágenes,
        resaltarEnlaces,
        detenerAnimaciones,
        modoLupa,
        lectorPantalla,
        descripcionAudio,
        subtitulosCerrados,
        subtitulosEnVivo,
        transcripcion,
        controlesAudio,
        alertasVisuales,
        skipLinkActivo,
        toggleContraste,
        toggleDislexia,
        aumentarTexto,
        disminuirTexto,
        toggleOcultarImágenes,
        toggleResaltarEnlaces,
        toggleAnimaciones,
        toggleModoLupa,
        toggleInvertirColores,
        setEspaciadoNivel,
        setAlturaLinea,
        setLectorPantalla,
        setDescripcionAudio,
        setSubtitulosCerrados,
        setSubtitulosEnVivo,
        setTranscripcion,
        setControlesAudio,
        setAlertasVisuales,
        setSkipLinkActivo
      }}
    >
      <ThemeProvider theme={theme}>
        <div
          style={{
            backgroundColor: altoContraste ? '#000' : '#fff',
            color: altoContraste ? '#fff' : '#000',
            fontFamily: modoDislexia ? 'Lexend Deca, Arial, sans-serif' : 'Arial, sans-serif',
            transition: 'all 0.3s ease-in-out',
            padding: '0 1rem'
          }}
        >
          {children}
        </div>
      </ThemeProvider>
    </AccessibilityContext.Provider>
  );
};

export const useAccessibility = () => useContext(AccessibilityContext);
