import React from 'react';
import { Box, Grid, Typography, Chip } from '@mui/material';
import ProductoCard from '../../components/productos/ProductoCard';
import { useNavigate } from 'react-router-dom';

const productosBanano = [
  {
    id: 1,
    nombre: 'Bolsas Microperforadas',
    categoria: 'Banano',
    descripcion: 'Control de humedad y mejora la calidad del racimo.',
    imagen: '/imagenes/productos/bolsas-banano.webp',
    destacado: 'Nuevo'
  },
  {
    id: 2,
    nombre: 'Plásticos para Cobertura',
    categoria: 'Banano',
    descripcion: 'Protección ante clima extremo y plagas.',
    imagen: '/imagenes/productos/plasticos-banano.webp',
    destacado: 'Eco'
  },
  {
    id: 3,
    nombre: 'Tratamiento Foliar',
    categoria: 'Banano',
    descripcion: 'Estimula crecimiento y previene enfermedades.',
    imagen: '/imagenes/productos/foliar-banano.webp',
    destacado: null
  }
];

export default function Banano() {
  const navigate = useNavigate();

  return (
    <Box sx={{
      backgroundImage: 'linear-gradient(135deg, #f1f8e9 0%, #fff 100%)',
      px: 4,
      py: 6,
      mt: 8,
      borderRadius: 4,
      maxWidth: '1200px',
      mx: 'auto'
    }}>

      {/* Banner institucional */}
      <Box sx={{ mb: 4 }}>
        <img
          src="/imagenes/banner-banano.webp"
          alt="Productos para Banano"
          style={{ width: '100%', borderRadius: 12 }}
        />
      </Box>

      {/* Título principal */}
      <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#2e7d32', mb: 2 }} align="center">
        🍌 Productos para Banano
      </Typography>
      <Typography variant="body1" sx={{ color: 'text.secondary', textAlign: 'center', mb: 4 }}>
        Soluciones confiables para el cultivo de banano con enfoque sostenible y técnico
      </Typography>

      {/* Grid de productos */}
      <Grid container spacing={4}>
        {productosBanano.map((producto) => (
          <Grid item xs={12} sm={6} md={4} key={producto.id}>
            <Box
              onClick={() => navigate(`/productos/detalle/${producto.id}`)}
              sx={{
                position: 'relative',
                cursor: 'pointer',
                '&:hover': {
                  transform: 'scale(1.02)',
                  transition: 'transform 0.3s ease'
                }
              }}
            >
              <ProductoCard producto={producto} />
              {producto.destacado && (
                <Chip
                  label={producto.destacado}
                  color="success"
                  size="small"
                  sx={{ position: 'absolute', top: 12, left: 12 }}
                />
              )}
            </Box>
          </Grid>
        ))}
      </Grid>

      {/* Sección institucional de asesoría */}
      <Box sx={{
        mt: 6,
        p: 4,
        backgroundColor: '#e8f5e9',
        borderRadius: 4,
        textAlign: 'center',
        boxShadow: '0 4px 14px rgba(0,0,0,0.1)'
      }}>
        <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 1 }}>
          ¿Necesitás asesoría personalizada?
        </Typography>
        <Typography variant="body2" sx={{ mb: 2 }}>
          Nuestro equipo técnico puede ayudarte a seleccionar el producto ideal para tu cultivo 🍃
        </Typography>
        <Chip
          label="Solicitar asesoría"
          color="primary"
          clickable
          onClick={() => navigate('/contacto')}
        />
      </Box>
    </Box>
  );
}
