// AccessibilityFAB.jsx (UserWay avanzado)
import React, { useState, useContext } from 'react';
import {
  Box,
  Fab,
  Drawer,
  Typography,
  Grid,
  IconButton,
  ToggleButtonGroup,
  ToggleButton,
  Divider,
  Select,
  MenuItem
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ContrastIcon from '@mui/icons-material/Tonality';
import PsychologyIcon from '@mui/icons-material/Psychology';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import ImageIcon from '@mui/icons-material/Image';
import AnimationIcon from '@mui/icons-material/MotionPhotosOff';
import LinkIcon from '@mui/icons-material/Link';
import MouseIcon from '@mui/icons-material/Mouse';
import VisibilityIcon from '@mui/icons-material/Visibility';
import FormatSizeIcon from '@mui/icons-material/FormatSize';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import InvertColorsIcon from '@mui/icons-material/InvertColors';
import FormatLineSpacingIcon from '@mui/icons-material/FormatLineSpacing';
import LanguageIcon from '@mui/icons-material/Language';
import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [language, setLanguage] = useState('es');
  const [espaciadoNivel, setEspaciadoNivel] = useState('normal');
  const [lineHeight, setLineHeight] = useState('normal');

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    ocultarImágenes,
    resaltarEnlaces,
    detenerAnimaciones,
    modoLupa,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto,
    toggleOcultarImágenes,
    toggleResaltarEnlaces,
    toggleAnimaciones,
    toggleModoLupa,
    toggleMonocromatico,
    leerTexto
  } = useContext(AccessibilityContext);

  const handleLanguageChange = (event) => {
    setLanguage(event.target.value);
    // aquí podrías cambiar traducción si tienes i18n
  };

  const handleEspaciadoNivel = (event, newLevel) => {
    if (newLevel) setEspaciadoNivel(newLevel);
  };

  const handleLineHeight = (event, newHeight) => {
    if (newHeight) setLineHeight(newHeight);
  };

  const renderOption = (icon, label, onClick) => (
    <Box
      onClick={onClick}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        p: 2,
        backgroundColor: '#f5f5f5',
        borderRadius: 2,
        cursor: 'pointer',
        '&:hover': {
          backgroundColor: '#e0e0e0'
        }
      }}
    >
      <IconButton color="primary">{icon}</IconButton>
      <Typography variant="caption" align="center">
        {label}
      </Typography>
    </Box>
  );

  return (
    <>
      <Fab
        onClick={() => setOpen(true)}
        sx={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          backgroundColor: '#000',
          color: '#fff',
          zIndex: 1300,
          '&:hover': {
            backgroundColor: '#333'
          }
        }}
        aria-label="Accesibilidad"
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>

      <Drawer
        anchor="right"
        open={open}
        onClose={() => setOpen(false)}
        PaperProps={{ sx: { width: 360, p: 3 } }}
      >
        <Typography variant="h6" gutterBottom>
          Menú De Accesibilidad
        </Typography>

        <Box display="flex" alignItems="center" gap={1} mb={2}>
          <LanguageIcon color="primary" />
          <Select
            value={language}
            onChange={handleLanguageChange}
            size="small"
            variant="outlined"
            sx={{ minWidth: 180 }}
          >
            <MenuItem value="es">Español (México)</MenuItem>
            <MenuItem value="en">English</MenuItem>
          </Select>
        </Box>

        <Divider sx={{ mb: 2 }} />

        <Grid container spacing={2}>
          <Grid item xs={6}>{renderOption(<InvertColorsIcon />, 'Invertir colores', toggleMonocromatico)}</Grid>
          <Grid item xs={6}>{renderOption(<VolumeUpIcon />, 'Leer página', () => leerTexto(document.body.innerText.slice(0, 500)))}</Grid>
          <Grid item xs={6}>{renderOption(<ContrastIcon />, 'Alto contraste', toggleContraste)}</Grid>
          <Grid item xs={6}>{renderOption(<LinkIcon />, 'Resaltar enlaces', toggleResaltarEnlaces)}</Grid>
          <Grid item xs={6}>{renderOption(<FormatSizeIcon />, 'Aumentar texto', aumentarTexto)}</Grid>
          <Grid item xs={6}>{renderOption(<VisibilityIcon />, 'Reducir texto', disminuirTexto)}</Grid>
          <Grid item xs={6}>{renderOption(<TextFieldsIcon />, 'Espaciado texto', () => {})}</Grid>
          <Grid item xs={6}>{renderOption(<AnimationIcon />, 'Detener animaciones', toggleAnimaciones)}</Grid>
          <Grid item xs={6}>{renderOption(<ImageIcon />, 'Ocultar imágenes', toggleOcultarImágenes)}</Grid>
          <Grid item xs={6}>{renderOption(<PsychologyIcon />, 'Dislexia', toggleDislexia)}</Grid>
          <Grid item xs={6}>{renderOption(<MouseIcon />, 'Modo lupa', toggleModoLupa)}</Grid>
          <Grid item xs={6}>{renderOption(<FormatLineSpacingIcon />, 'Altura línea', () => {})}</Grid>
        </Grid>

        {/* Espaciado ligero visual */}
        <Box mt={3}>
          <Typography variant="caption" sx={{ fontWeight: 600 }} gutterBottom>
            Nivel de espaciado
          </Typography>
          <ToggleButtonGroup
            size="small"
            value={espaciadoNivel}
            exclusive
            onChange={handleEspaciadoNivel}
            fullWidth
          >
            <ToggleButton value="normal">•</ToggleButton>
            <ToggleButton value="medio">••</ToggleButton>
            <ToggleButton value="alto">•••</ToggleButton>
          </ToggleButtonGroup>
        </Box>

        {/* Line height visual */}
        <Box mt={2}>
          <Typography variant="caption" sx={{ fontWeight: 600 }} gutterBottom>
            Altura de línea
          </Typography>
          <ToggleButtonGroup
            size="small"
            value={lineHeight}
            exclusive
            onChange={handleLineHeight}
            fullWidth
          >
            <ToggleButton value="normal">1x</ToggleButton>
            <ToggleButton value="medio">1.5x</ToggleButton>
            <ToggleButton value="alto">2x</ToggleButton>
          </ToggleButtonGroup>
        </Box>
      </Drawer>
    </>
  );
}
