import React, { useState } from 'react';
import {
  Box,
  Fab,
  Drawer,
  Typography,
  Grid,
  IconButton,
  Button
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ContrastIcon from '@mui/icons-material/Tonality';
import PsychologyIcon from '@mui/icons-material/Psychology';
import ImageIcon from '@mui/icons-material/Image';
import AnimationIcon from '@mui/icons-material/MotionPhotosOff';
import LinkIcon from '@mui/icons-material/Link';
import MouseIcon from '@mui/icons-material/Mouse';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import InvertColorsIcon from '@mui/icons-material/InvertColors';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import { useAccessibility } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);

  const {
    panelAbierto,
    setPanelAbierto,
    tamañoTexto,
    espaciadoTexto,
    ocultarImágenes,
    resaltarEnlaces,
    detenerAnimaciones,
    modoLupa,
    modoMonocromatico,
    altoContraste,
    modoDislexia,
    toggleContraste,
    toggleDislexia,
    toggleOcultarImágenes,
    toggleResaltarEnlaces,
    toggleAnimaciones,
    toggleModoLupa,
    toggleMonocromatico,
    leerTexto,
    setTamañoTexto,
    setEspaciadoTexto
  } = useAccessibility();

  const handleOpen = () => {
    setOpen(true);
    setPanelAbierto(true);
  };

  const handleClose = () => {
    setOpen(false);
    setPanelAbierto(false);
  };

  return (
    <>
      <Fab
        onClick={handleOpen}
        sx={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          backgroundColor: '#000',
          color: '#fff',
          zIndex: 1300,
          '&:hover': {
            backgroundColor: '#333'
          }
        }}
        aria-label="Accesibilidad"
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>

      <Drawer
        anchor="right"
        open={open}
        onClose={handleClose}
        PaperProps={{ sx: { width: 360, p: 3 } }}
      >
        {/* 🌐 Selector de idioma (placeholder) */}
        <Box sx={{ mb: 3, display: 'flex', justifyContent: 'center', gap: 1 }}>
          <Button variant="outlined" size="small">🇪🇸 Español</Button>
          <Button variant="outlined" size="small">🇺🇸 English</Button>
        </Box>

        <Typography variant="h6" gutterBottom>
          Menú de Accesibilidad
        </Typography>

        {/* 🔠 Tamaño de texto con niveles */}
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" sx={{ mb: 1, fontWeight: 'bold' }}>
            <TextFieldsIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
            Tamaño de texto
          </Typography>
          <Box sx={{ display: 'flex', gap: 1 }}>
            {[1, 1.2, 1.4].map((valor) => (
              <Button
                key={valor}
                onClick={() => setTamañoTexto(valor)}
                variant={tamañoTexto === valor ? 'contained' : 'outlined'}
                sx={{
                  width: 36,
                  height: 36,
                  borderRadius: 1,
                  fontSize: '0.75rem',
                  minWidth: 0
                }}
              >
                A{valor === 1 ? '' : '+'}
              </Button>
            ))}
          </Box>
        </Box>

        {/* 🔡 Espaciado de texto con niveles */}
        <Box sx={{ mb: 3 }}>
          <Typography variant="body2" sx={{ mb: 1, fontWeight: 'bold' }}>
            <TextFieldsIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
            Espaciado de texto
          </Typography>
          <Box sx={{ display: 'flex', gap: 1 }}>
            {[1, 1.3, 1.6].map((valor) => (
              <Button
                key={valor}
                onClick={() => setEspaciadoTexto(valor)}
                variant={espaciadoTexto === valor ? 'contained' : 'outlined'}
                sx={{
                  width: 36,
                  height: 36,
                  borderRadius: 1,
                  fontSize: '0.75rem',
                  minWidth: 0
                }}
              >
                {valor}
              </Button>
            ))}
          </Box>
        </Box>

        {/* 🔧 Controles generales */}
        <Grid container spacing={2}>
          {[
            { icon: <InvertColorsIcon />, label: 'Invertir colores', onClick: toggleMonocromatico },
            { icon: <VolumeUpIcon />, label: 'Leer pantalla', onClick: () => leerTexto(document.body.innerText.slice(0, 500)) },
            { icon: <ContrastIcon />, label: 'Alto contraste', onClick: toggleContraste },
            { icon: <LinkIcon />, label: 'Resaltar enlaces', onClick: toggleResaltarEnlaces },
            { icon: <AnimationIcon />, label: 'Detener animaciones', onClick: toggleAnimaciones },
            { icon: <ImageIcon />, label: 'Ocultar imágenes', onClick: toggleOcultarImágenes },
            { icon: <PsychologyIcon />, label: 'Dislexia', onClick: toggleDislexia },
            { icon: <MouseIcon />, label: 'Modo lupa', onClick: toggleModoLupa }
          ].map((btn, index) => (
            <Grid item xs={6} key={index}>
              <Box
                onClick={btn.onClick}
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  p: 2,
                  backgroundColor: '#f5f5f5',
                  borderRadius: 2,
                  cursor: 'pointer',
                  '&:hover': {
                    backgroundColor: '#e0e0e0'
                  }
                }}
              >
                <IconButton color="primary">{btn.icon}</IconButton>
                <Typography variant="caption" align="center">
                  {btn.label}
                </Typography>
              </Box>
            </Grid>
          ))}
        </Grid>
      </Drawer>
    </>
  );
}
