import React from 'react';
import {
  TextField,
  Button,
  Typography,
  Container,
  Box,
  InputAdornment,
  IconButton
} from '@mui/material';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

const schema = yup.object().shape({
  nombre: yup.string().required('El nombre es obligatorio'),
  correo: yup.string().email('Correo inválido').required('El correo es obligatorio'),
  contraseña: yup.string().min(6, 'Debe tener al menos 6 caracteres').required(),
  confirmar: yup.string()
    .oneOf([yup.ref('contraseña'), null], 'Las contraseñas deben coincidir')
    .required()
});

export default function RegistroForm() {
  const [mostrarPass, setMostrarPass] = React.useState(false);
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (data) => {
    console.log('Datos del formulario:', data);
    // Aquí iría la llamada al backend con Axios
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 5 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Registro de Usuario
        </Typography>

        <form onSubmit={handleSubmit(onSubmit)} noValidate aria-label="Formulario de registro">
          <TextField
            label="Nombre completo"
            fullWidth
            margin="normal"
            {...register('nombre')}
            error={!!errors.nombre}
            helperText={errors.nombre?.message}
            inputProps={{ 'aria-label': 'Campo nombre' }}
          />

          <TextField
            label="Correo electrónico"
            fullWidth
            margin="normal"
            {...register('correo')}
            error={!!errors.correo}
            helperText={errors.correo?.message}
            inputProps={{ 'aria-label': 'Campo correo electrónico' }}
          />

          <TextField
            label="Contraseña"
            fullWidth
            margin="normal"
            type={mostrarPass ? 'text' : 'password'}
            {...register('contraseña')}
            error={!!errors.contraseña}
            helperText={errors.contraseña?.message}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={() => setMostrarPass((prev) => !prev)}>
                    {mostrarPass ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
            inputProps={{ 'aria-label': 'Campo contraseña' }}
          />

          <TextField
            label="Confirmar contraseña"
            fullWidth
            margin="normal"
            type="password"
            {...register('confirmar')}
            error={!!errors.confirmar}
            helperText={errors.confirmar?.message}
            inputProps={{ 'aria-label': 'Campo confirmar contraseña' }}
          />

          <Button variant="contained" color="primary" type="submit" sx={{ mt: 3 }} fullWidth>
            Registrarse
          </Button>
        </form>
      </Box>
    </Container>
  );
}
