import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  Link,
  TextField,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddBoxIcon from '@mui/icons-material/AddBox';
import EditIcon from '@mui/icons-material/Edit';
import LoginModal from '../../components/modals/LoginModal';

export default function Comprar() {
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('token'));
  const rol = localStorage.getItem('rol')?.trim()?.toLowerCase();
  const esAdmin = rol === 'admin';

  const categorias = [
    'Alimento Perro',
    'Alimento Gato',
    'Limpieza Y Aseo',
    'JardinerÃ­a'
  ];

  const [productos, setProductos] = useState([]);
  const [showLogin, setShowLogin] = useState(false);
  const [carrito, setCarrito] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/productos')
      .then(res => res.json())
      .then(data => setProductos(data))
      .catch(err => console.error('Error al cargar productos:', err));
  }, []);

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      setShowLogin(true);
    } else {
      const seleccionado = productos.find(p => p._id === id);
      if (seleccionado) {
        setCarrito(prev => [...prev, seleccionado]);
      }
    }
  };

  const total = carrito.reduce((acc, prod) => acc + (prod.precio || 0), 0);

  const renderProductos = (lista) => (
    <Grid container spacing={4}>
      {lista.map(prod => (
        <Grid item xs={12} sm={6} md={3} key={prod._id}>
          <Paper elevation={0} sx={{ p: 2, borderRadius: 2, position: 'relative' }}>
            {prod.descuento && (
              <Box sx={{
                position: 'absolute',
                top: 12,
                right: 12,
                bgcolor: '#f44336',
                color: '#fff',
                px: 1.5,
                py: 0.5,
                borderRadius: 1,
                fontSize: '0.75rem'
              }}>
                -{prod.descuento}%
              </Box>
            )}

            <Box
              component="img"
              src={prod.imagen}
              alt={prod.nombre}
              sx={{
                width: '100%',
                borderRadius: 2,
                objectFit: 'cover'
              }}
            />

            <Box sx={{ mt: 1, display: 'flex', alignItems: 'baseline', gap: 1 }}>
              <Typography sx={{ color: '#d32f2f', fontWeight: 'bold', fontSize: '1.1rem' }}>
                {typeof prod.precio === 'number'
                  ? `$${prod.precio.toFixed(2)}`
                  : 'Precio no disponible'}
              </Typography>
              {typeof prod.precioOriginal === 'number' && (
                <Typography sx={{ color: '#999', textDecoration: 'line-through', fontSize: '0.85rem' }}>
                  ${prod.precioOriginal.toFixed(2)}
                </Typography>
              )}
            </Box>

            <Typography variant="caption" sx={{ color: '#666', display: 'block', mb: 1 }}>
              (Exento de IVA)
            </Typography>

            <Link
              href={`/productos/detalle/${prod.categoria}/${prod._id}`}
              sx={{
                display: 'block',
                fontWeight: 'bold',
                color: '#388e3c',
                textDecoration: 'underline',
                mb: 1,
                fontSize: '0.875rem'
              }}
            >
              {prod.nombre}
            </Link>

            <Button
              fullWidth
              onClick={() => handleCompra(prod._id)}
              sx={{
                border: '1px solid #fb8c00',
                color: '#fb8c00',
                textTransform: 'none',
                borderRadius: 2,
                py: 1
              }}
            >
              Agregar
            </Button>

            {esAdmin && (
              <>
                <Button
                  fullWidth
                  variant="outlined"
                  startIcon={<EditIcon />}
                  onClick={() => navigate(`/productos/editar/${prod._id}`)}
                  sx={{
                    mt: 1,
                    borderRadius: 2,
                    fontWeight: 'bold',
                    color: '#1976d2',
                    borderColor: '#1976d2',
                    textTransform: 'none',
                    '&:hover': { bgcolor: '#e3f2fd' }
                  }}
                >
                  Editar producto
                </Button>

                <Button
                  fullWidth
                  variant="outlined"
                  onClick={() => navigate(`/productos/eliminar/${prod._id}`)}
                  sx={{
                    mt: 1,
                    borderRadius: 2,
                    fontWeight: 'bold',
                    color: '#d32f2f',
                    borderColor: '#d32f2f',
                    textTransform: 'none',
                    '&:hover': { bgcolor: '#ffebee' }
                  }}
                >
                  ðï¸ Eliminar producto
                </Button>
              </>
            )}
          </Paper>
        </Grid>
      ))}
    </Grid>
  );

  return (
    <Box sx={{ pt: 12, pb: 6, px: { xs: 2, md: 4 } }}>
      {/* Header institucional */}
      <Box sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bgcolor: 'white',
        borderBottom: '1px solid #ddd',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        px: 3,
        py: 1,
        zIndex: 1000,
      }}>
        <Typography
          variant="h6"
          component="a"
          href="/"
          sx={{
            color: '#2e7d32',
            fontWeight: 'bold',
            textDecoration: 'none',
            fontSize: '1.3rem',
            cursor: 'pointer',
            '&:hover': { textDecoration: 'underline' }
          }}
        >
          Mercado AgrÃ­cola
        </Typography>

        <TextField
          size="small"
          placeholder="Buscar producto"
          sx={{
            bgcolor: '#f1f8e9',
            borderRadius: 20,
            width: { xs: 140, md: 240 },
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon sx={{ color: 'gray' }} />
              </InputAdornment>
            )
          }}
        />

        {isLoggedIn ? (
          <Button
            variant="outlined"
            onClick={() => {
              localStorage.removeItem('token');
              localStorage.removeItem('rol');
              window.location.reload();
            }}
            sx={{
              color: '#d32f2f',
              borderColor: '#d32f2f',
              fontWeight: 'bold',
              textTransform: 'uppercase',
              fontSize: '0.75rem',
            }}
          >
            Cerrar sesiÃ³n
          </Button>
        ) : (
          <Button
            variant="outlined"
            onClick={() => setShowLogin(true)}
            sx={{
              color: '#2e7d32',
              borderColor: '#2e7d32',
              fontWeight: 'bold',
              textTransform: 'uppercase',
              fontSize: '0.75rem',
            }}
          >
            Iniciar sesiÃ³n
          </Button>
        )}

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Typography sx={{ fontSize: '1.2rem' }}>ð</Typography>
          <Typography sx={{ fontWeight: 'bold', fontSize: '0.9rem' }}>
            {carrito.length} | ${total.toFixed(2)}
          </Typography>
        </Box>
      </Box>

      {/* BotÃ³n solo para admins */}
      {esAdmin && (
        <Box sx={{ my: 5, textAlign: 'center' }}>
          <Button
            variant="contained"
            onClick={() => navigate('/productos/nuevo')}
            startIcon={<AddBoxIcon />}
            sx={{
              bgcolor: '#388e3c',
              fontWeight: 'bold',
              px: 4,
              py: 1.5,
              borderRadius: 2,
              '&:hover': { bgcolor: '#2e7d32' }
            }}
          >
            Registrar producto
          </Button>
        </Box>
      )}

      {/* ð¬ CatÃ¡logo por categorÃ­as */}
      <Typography variant="h5" sx={{ fontWeight: 'bold', mt: 6, mb: 3 }}>
        ð¬ CATÃLOGO POR CATEGORÃAS
      </Typography>

      {categorias.map(categoria => {
        const productosPorCategoria = productos.filter(p => p.categoria === categoria);
        if (productosPorCategoria.length === 0) return null;

        return (
          <Box key={categoria} sx={{ mb: 6 }}>
            <Typography
              variant="h6"
              sx={{
                fontWeight: 'bold',
                color: '#2e7d32',
                mb: 2,
                borderLeft: '5px solid #66bb6a',
                pl: 2,
                fontSize: '1.2rem',
                textTransform: 'uppercase'
              }}
            >
              {categoria}
            </Typography>
            {renderProductos(productosPorCategoria)}
          </Box>
        );
      })}

      {/* Modal de login */}
      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
    </Box>
  );
}
