import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Alert,
  Paper,
  Stack,
} from '@mui/material';

export default function EditarProductoForm() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [producto, setProducto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Form fields
  const [nombre, setNombre] = useState('');
  const [categoria, setCategoria] = useState('');
  const [precio, setPrecio] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [imagen, setImagen] = useState('');
  const [descuento, setDescuento] = useState('');

  useEffect(() => {
    async function fetchProducto() {
      try {
        setLoading(true);
        const res = await fetch(`http://localhost:5000/api/productos/${id}`);
        if (!res.ok) throw new Error('No se pudo cargar el producto');
        const data = await res.json();
        setProducto(data);

        setNombre(data.nombre || '');
        setCategoria(data.categoria || '');
        setPrecio(data.precio?.toString() || '');
        setDescripcion(data.descripcion || '');
        setImagen(data.imagen || '');
        setDescuento(data.descuento?.toString() || '');
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchProducto();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!nombre.trim() || !categoria.trim() || !precio) {
      setError('Por favor completa todos los campos obligatorios.');
      return;
    }

    if (isNaN(precio) || parseFloat(precio) < 0) {
      setError('El precio debe ser un número positivo.');
      return;
    }

    if (descuento && (isNaN(descuento) || descuento < 0 || descuento > 100)) {
      setError('El descuento debe ser un número entre 0 y 100.');
      return;
    }

    const updatedProduct = {
      nombre,
      categoria,
      precio: parseFloat(precio),
      descripcion,
      imagen,
      descuento: descuento ? parseFloat(descuento) : 0,
    };

    try {
      const res = await fetch(`http://localhost:5000/api/productos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedProduct),
      });

      if (!res.ok) throw new Error('Error al actualizar el producto');

      alert('Producto actualizado con éxito');
      navigate('/comprar');
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 10 }}>
        <CircularProgress color="success" />
      </Box>
    );
  }

  return (
    <Paper
      elevation={6}
      sx={{
        maxWidth: 650,
        mx: 'auto',
        mt: 8,
        p: 4,
        borderRadius: 3,
        bgcolor: '#f9fdf8',
        boxShadow: '0 8px 20px rgba(0, 100, 0, 0.1)',
      }}
    >
      <Typography
        variant="h4"
        sx={{
          color: '#2e7d32',
          fontWeight: '700',
          mb: 4,
          textAlign: 'center',
          fontFamily: "'Poppins', sans-serif",
          textShadow: '0 2px 6px rgba(46, 125, 50, 0.5)',
        }}
      >
        Editar Producto
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3, fontWeight: '600' }}>
          {error}
        </Alert>
      )}

      <Box
        component="form"
        noValidate
        autoComplete="off"
        onSubmit={handleSubmit}
        sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}
      >
        <TextField
          label="Nombre del producto *"
          variant="outlined"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          fullWidth
          required
          inputProps={{ maxLength: 80 }}
          sx={{
            '& .MuiOutlinedInput-root': {
              '&.Mui-focused fieldset': { borderColor: '#388e3c' },
            },
          }}
        />

        <TextField
          label="Categoría *"
          variant="outlined"
          value={categoria}
          onChange={(e) => setCategoria(e.target.value)}
          fullWidth
          required
          helperText="Ejemplo: Mascotas, Jardinería, Químicos"
          sx={{
            '& .MuiOutlinedInput-root': {
              '&.Mui-focused fieldset': { borderColor: '#388e3c' },
            },
          }}
        />

        <TextField
          label="Precio *"
          variant="outlined"
          type="number"
          value={precio}
          onChange={(e) => setPrecio(e.target.value)}
          fullWidth
          required
          inputProps={{ min: 0, step: '0.01' }}
          sx={{
            '& .MuiOutlinedInput-root': {
              '&.Mui-focused fieldset': { borderColor: '#388e3c' },
            },
          }}
        />

        <TextField
          label="Descripción"
          variant="outlined"
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
          fullWidth
          multiline
          rows={4}
          inputProps={{ maxLength: 500 }}
          sx={{
            '& .MuiOutlinedInput-root': {
              '&.Mui-focused fieldset': { borderColor: '#388e3c' },
            },
          }}
        />

        <TextField
          label="URL de imagen"
          variant="outlined"
          value={imagen}
          onChange={(e) => setImagen(e.target.value)}
          fullWidth
          placeholder="https://"
          sx={{
            '& .MuiOutlinedInput-root': {
              '&.Mui-focused fieldset': { borderColor: '#388e3c' },
            },
          }}
        />

        <TextField
          label="Descuento (%)"
          variant="outlined"
          type="number"
          value={descuento}
          onChange={(e) => setDescuento(e.target.value)}
          fullWidth
          inputProps={{ min: 0, max: 100, step: 1 }}
          helperText="Valor entre 0 y 100"
          sx={{
            '& .MuiOutlinedInput-root': {
              '&.Mui-focused fieldset': { borderColor: '#388e3c' },
            },
          }}
        />

        <Stack direction="row" justifyContent="space-between" sx={{ mt: 2 }}>
          <Button
            variant="outlined"
            color="error"
            onClick={() => navigate(-1)}
            sx={{ px: 4, fontWeight: '600' }}
          >
            Cancelar
          </Button>
          <Button
            type="submit"
            variant="contained"
            sx={{
              px: 4,
              bgcolor: '#2e7d32',
              fontWeight: '700',
              '&:hover': { bgcolor: '#1b4d19' },
            }}
          >
            Guardar Cambios
          </Button>
        </Stack>
      </Box>
    </Paper>
  );
}
