import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  IconButton,
  InputAdornment,
  Snackbar,
  Alert
} from '@mui/material';
import { useForm } from 'react-hook-form';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';

export default function LoginForm({ onClose }) {
  const {
    register,
    handleSubmit,
    formState: { errors },
    setError
  } = useForm();

  const [mostrarClave, setMostrarClave] = React.useState(false);
  const [errorLogin, setErrorLogin] = React.useState('');

  const onSubmit = (data) => {
    // Simulación de login (puedes conectar con backend aquí)
    if (data.email !== 'demo@usuario.com' || data.password !== '1234') {
      setError('email', { message: '' }); // Limpiar errores previos
      setError('password', { message: '' });
      setErrorLogin('Usuario o contraseña incorrectos');
    } else {
      console.log('Inicio de sesión exitoso:', data);
      if (onClose) onClose(); // cerrar modal si viene desde un modal
    }
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit(onSubmit)}
      noValidate
      aria-labelledby="titulo-login"
      role="form"
      sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}
    >
      <Typography id="titulo-login" variant="h5" sx={{ fontWeight: 'bold' }}>
        Iniciar sesión
      </Typography>

      <TextField
        label="Correo electrónico"
        type="email"
        fullWidth
        autoFocus
        {...register('email', { required: 'Este campo es obligatorio' })}
        error={!!errors.email}
        helperText={errors.email?.message}
        inputProps={{
          'aria-label': 'Campo correo electrónico',
          autoComplete: 'username'
        }}
      />

      <TextField
        label="Contraseña"
        type={mostrarClave ? 'text' : 'password'}
        fullWidth
        {...register('password', { required: 'Ingresa tu contraseña' })}
        error={!!errors.password}
        helperText={errors.password?.message}
        inputProps={{
          'aria-label': 'Campo contraseña',
          autoComplete: 'current-password'
        }}
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton
                onClick={() => setMostrarClave(!mostrarClave)}
                aria-label={mostrarClave ? 'Ocultar contraseña' : 'Mostrar contraseña'}
              >
                {mostrarClave ? <VisibilityOffIcon /> : <VisibilityIcon />}
              </IconButton>
            </InputAdornment>
          )
        }}
      />

      <Button
        type="submit"
        variant="contained"
        color="primary"
        fullWidth
        sx={{ mt: 2 }}
        aria-label="Botón para iniciar sesión"
      >
        Iniciar sesión
      </Button>

      <Snackbar
        open={!!errorLogin}
        autoHideDuration={4000}
        onClose={() => setErrorLogin('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert severity="error" variant="filled" onClose={() => setErrorLogin('')}>
          {errorLogin}
        </Alert>
      </Snackbar>
    </Box>
  );
}
