import React from 'react';
import { useParams } from 'react-router-dom';
import { Box, Grid, Typography, Button, Chip, Breadcrumbs, Link } from '@mui/material';
import { useAccessibility } from '../../context/AccessibilityContext';
import productos from '../../data/productos.json';

export default function ProductoDetalle() {
  const { id } = useParams();
  const { altoContraste } = useAccessibility();

  const producto = productos.find(p => String(p.id) === id);

  if (!producto) {
    return (
      <Box sx={{ mt: 8, px: 4, py: 6 }}>
        <Typography variant="h6" color="error">Producto no encontrado</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{
      mt: 8,
      px: 4,
      py: 6,
      maxWidth: '1200px',
      mx: 'auto',
      backgroundColor: altoContraste ? '#101010' : '#fff',
      color: altoContraste ? '#fff' : '#000',
      borderRadius: 4,
      boxShadow: altoContraste
        ? '0 0 0 2px #ffd900'
        : '0 6px 20px rgba(0,0,0,0.1)'
    }}>

      {/* Breadcrumb */}
      <Breadcrumbs separator="›" sx={{ mb: 3 }}>
        <Link href="/" color={altoContraste ? '#ffd900' : '#2e7d32'}>Inicio</Link>
        <Link
          href={`/productos/${producto.categoria.toLowerCase()}`}
          color={altoContraste ? '#ffd900' : '#2e7d32'}
        >
          {producto.categoria}
        </Link>
        <Typography>{producto.nombre}</Typography>
      </Breadcrumbs>

      {/* Layout institucional */}
      <Grid container spacing={4}>
        {/* Imagen */}
        <Grid item xs={12} md={5}>
          <Box sx={{ width: '100%', height: '100%' }}>
            <img
              src={producto.imagen}
              alt={producto.nombre}
              style={{
                width: '100%',
                borderRadius: 12,
                boxShadow: altoContraste
                  ? '0 0 0 2px #ffd900'
                  : '0 4px 14px rgba(0,0,0,0.1)'
              }}
            />
          </Box>
        </Grid>

        {/* Contenido técnico */}
        <Grid item xs={12} md={7}>
          <Typography variant="h3" sx={{ fontWeight: 'bold', mb: 2 }}>
            {producto.nombre}
          </Typography>
          <Typography variant="subtitle2" sx={{ mb: 1, color: altoContraste ? '#ffd900' : '#2e7d32' }}>
            {producto.categoria}
          </Typography>
          {producto.destacado && (
            <Chip label={producto.destacado} color="success" sx={{ mb: 2 }} />
          )}

          <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.8, maxWidth: '600px' }}>
            {producto.descripcion}
          </Typography>

          <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
            {producto.botones?.map((btn, index) => (
              <Button
                key={index}
                variant="contained"
                href={btn.url}
                target="_blank"
                sx={{
                  bgcolor: altoContraste ? '#ffd900' : '#2e7d32',
                  color: altoContraste ? '#000' : '#fff',
                  fontWeight: 'bold',
                  '&:hover': {
                    bgcolor: altoContraste ? '#ffeb3b' : '#1b5e20'
                  }
                }}
              >
                {btn.label}
              </Button>
            ))}
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
}
