import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  TextField,
  InputAdornment,
  Link
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddBoxIcon from '@mui/icons-material/AddBox';
import EditIcon from '@mui/icons-material/Edit';
import { useNavigate } from 'react-router-dom';
import LoginModal from '../../components/modals/LoginModal';

export default function Comprar() {
  const navigate = useNavigate();
  const rol = localStorage.getItem('rol')?.trim()?.toLowerCase();
  const esAdmin = rol === 'admin';
  const isLoggedIn = Boolean(localStorage.getItem('token'));

  const [productos, setProductos] = useState([]);
  const [carrito, setCarrito] = useState([]);
  const [showLogin, setShowLogin] = useState(false);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('');
  const categoriasVisuales = ['Alimento Perro', 'Alimento Gato', 'Limpieza Y Aseo', 'JardinerÃ­a', 'Mascotas'];

  useEffect(() => {
    fetch('http://localhost:5000/api/productos')
      .then(res => res.json())
      .then(data => setProductos(data))
      .catch(err => console.error('Error al cargar productos:', err));
  }, []);

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      setShowLogin(true);
    } else {
      const p = productos.find(p => p._id === id);
      if (p) setCarrito(prev => [...prev, p]);
    }
  };

  const total = carrito.reduce((acc, p) => acc + (p.precio || 0), 0);

  const renderProductos = (lista) => (
    <Grid container spacing={4}>
      {lista.map(prod => (
        <Grid item xs={12} sm={6} md={3} key={prod._id}>
          <Paper elevation={3} sx={{
            p: 2,
            borderRadius: 3,
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
            position: 'relative'
          }}>
            {prod.descuento && (
              <Box sx={{
                position: 'absolute',
                top: 12,
                right: 12,
                bgcolor: '#f44336',
                color: '#fff',
                px: 1.5,
                py: 0.5,
                borderRadius: 1,
                fontSize: '0.75rem'
              }}>
                -{prod.descuento}%
              </Box>
            )}
            <Box
              component="img"
              src={prod.imagen}
              alt={prod.nombre}
              sx={{
                height: 180,
                width: '100%',
                objectFit: 'cover',
                borderRadius: 2,
                mb: 2
              }}
            />
            <Box>
              <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 1 }}>
                {prod.nombre}
              </Typography>
              <Typography variant="body2" sx={{ color: '#555' }}>
                ð CategorÃ­a: {prod.categoria}
              </Typography>
              <Typography variant="body2" sx={{ color: '#555', mb: 1 }}>
                ð¦ Ventas: {prod.ventas || 0}
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'baseline', gap: 1 }}>
                <Typography sx={{ color: '#d32f2f', fontWeight: 'bold', fontSize: '1rem' }}>
                  {typeof prod.precio === 'number' ? `$${prod.precio.toFixed(2)}` : 'Precio no disponible'}
                </Typography>
                {typeof prod.precioOriginal === 'number' && (
                  <Typography sx={{ color: '#999', textDecoration: 'line-through', fontSize: '0.85rem' }}>
                    ${prod.precioOriginal.toFixed(2)}
                  </Typography>
                )}
              </Box>
            </Box>
            <Button
              fullWidth
              onClick={() => handleCompra(prod._id)}
              sx={{
                mt: 2,
                border: '1px solid #fb8c00',
                color: '#fb8c00',
                textTransform: 'none',
                borderRadius: 2,
                py: 1,
                fontWeight: 'bold'
              }}
            >
              Agregar al carrito
            </Button>
            {esAdmin && (
              <Box sx={{ mt: 1, display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Button
                  fullWidth
                  variant="outlined"
                  startIcon={<EditIcon />}
                  onClick={() => navigate(`/productos/editar/${prod._id}`)}
                  sx={{ color: '#1976d2', borderColor: '#1976d2', fontWeight: 'bold' }}
                >
                  Editar
                </Button>
                <Button
                  fullWidth
                  variant="outlined"
                  onClick={() => navigate(`/productos/eliminar/${prod._id}`)}
                  sx={{ color: '#d32f2f', borderColor: '#d32f2f', fontWeight: 'bold' }}
                >
                  ðï¸ Eliminar
                </Button>
              </Box>
            )}
          </Paper>
        </Grid>
      ))}
    </Grid>
  );

  return (
    <Box sx={{ pt: 12, pb: 6, px: { xs: 2, md: 4 } }}>
      {/* ð Encabezado fijo */}
      <Box sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bgcolor: 'white',
        borderBottom: '1px solid #ddd',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        px: 3,
        py: 1,
        zIndex: 1000
      }}>
        <Typography
          variant="h6"
          component="a"
          href="/"
          sx={{
            color: '#2e7d32',
            fontWeight: 'bold',
            textDecoration: 'none',
            fontSize: '1.3rem'
          }}
        >
          Mercado AgrÃ­cola
        </Typography>
        <TextField
          size="small"
          placeholder="Buscar producto"
          sx={{ bgcolor: '#f1f8e9', borderRadius: 20, width: { xs: 140, md: 240 } }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon sx={{ color: 'gray' }} />
              </InputAdornment>
            )
          }}
        />
        {isLoggedIn ? (
          <Button
            variant="outlined"
            onClick={() => {
              localStorage.clear();
              window.location.reload();
            }}
            sx={{ color: '#d32f2f', borderColor: '#d32f2f', fontWeight: 'bold', fontSize: '0.75rem' }}
          >
            Cerrar sesiÃ³n
          </Button>
        ) : (
          <Button
            variant="outlined"
            onClick={() => setShowLogin(true)}
            sx={{ color: '#2e7d32', borderColor: '#2e7d32', fontWeight: 'bold', fontSize: '0.75rem' }}
          >
            Iniciar sesiÃ³n
          </Button>
        )}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Typography sx={{ fontSize: '1.2rem' }}>ð</Typography>
          <Typography sx={{ fontWeight: 'bold', fontSize: '0.9rem' }}>
            {carrito.length} | ${total.toFixed(2)}
          </Typography>
        </Box>
      </Box>

      {/* ð ï¸ Solo admins */}
      {esAdmin && (
        <Box sx={{ my: 5, textAlign: 'center' }}>
          <Button
            variant="contained"
            onClick={() => navigate('/productos/nuevo')}
            startIcon={<AddBoxIcon />}
            sx={{ bgcolor: '#388e3c', fontWeight: 'bold', px: 4, py: 1.5 }}
          >
            Registrar producto
          </Button>
        </Box>
      )}

      {/* ð Botones de categorÃ­a */}
      <Box sx={{ mb: 4, display: 'flex', gap: 2, flexWrap: 'wrap' }}>
        {categoriasVisuales.map(cat => (
          <Button
            key={cat}
            onClick={() => setCategoriaSeleccionada(cat)}
            variant={categoriaSeleccionada === cat ? 'contained' : 'outlined'}
            sx={{
              textTransform: 'none',
              borderRadius: 20,
              borderColor                : '#66bb6a',
              fontWeight: 'bold',
              px: 2,
              py: 1,
              borderRadius: 3,
              fontSize: '0.85rem'
            }}
          >
            {cat}
          </Button>
        ))}
        <Button
          onClick={() => setCategoriaSeleccionada('')}
          variant={categoriaSeleccionada === '' ? 'contained' : 'outlined'}
          sx={{
            textTransform: 'none',
            borderRadius: 20,
            borderColor: '#aaa',
            px: 2,
            py: 1,
            fontWeight: 'bold',
            bgcolor: '#f5f5f5',
            fontSize: '0.85rem'
          }}
        >
          Ver todo
        </Button>
      </Box>

      {/* ð¯ SecciÃ³n de productos */}
      <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 3 }}>
        {categoriaSeleccionada ? `Productos de ${categoriaSeleccionada}` : 'Todos los productos disponibles'}
      </Typography>
      {renderProductos(
        categoriaSeleccionada
          ? productos.filter(p => p.categoria?.trim() === categoriaSeleccionada)
