import React from 'react';
import {
  Box,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Snackbar,
  Alert
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import productos from '../../data/productos.json';

export default function EliminarProducto() {
  const { id } = useParams();
  const navigate = useNavigate();
  const rol = localStorage.getItem('rol');
  const esAdmin = rol === 'admin';

  const producto = productos.find(p => String(p.id) === String(id));
  const [confirmado, setConfirmado] = React.useState(false);
  const [eliminando, setEliminando] = React.useState(false);

  if (!esAdmin || !producto) {
    navigate('/'); // acceso restringido o producto no existe
    return null;
  }

  const handleEliminar = () => {
    setEliminando(true);

    // Simulación de eliminación lógica (por ejemplo: cambiar estado a inactivo)
    console.log(`Producto eliminado: ${producto.nombre} (ID: ${producto.id})`);

    setConfirmado(true);
    setTimeout(() => navigate('/comprar'), 2000);
  };

  const handleCancelar = () => navigate(-1);

  return (
    <>
      <Dialog open>
        <DialogTitle sx={{ fontWeight: 'bold', color: '#d32f2f' }}>
          ¿Deseás eliminar este producto?
        </DialogTitle>

        <DialogContent>
          <Typography sx={{ mb: 2 }}>
            Esta acción marcará el producto como <strong>inactivo</strong> en el sistema.
          </Typography>

          <Box sx={{ p: 2, bgcolor: '#f5f5f5', borderRadius: 2 }}>
            <Typography><strong>Nombre:</strong> {producto.nombre}</Typography>
            <Typography><strong>ID:</strong> {producto.id}</Typography>
            <Typography><strong>Precio:</strong> ${producto.precio}</Typography>
            <Typography sx={{ mt: 1, fontSize: '0.85rem', color: '#999' }}>
              (Esta acción puede revertirse desde el panel de administración)
            </Typography>
          </Box>
        </DialogContent>

        <DialogActions sx={{ justifyContent: 'space-between', px: 3, pb: 2 }}>
          <Button
            onClick={handleCancelar}
            variant="outlined"
            color="inherit"
            sx={{ textTransform: 'none' }}
          >
            Cancelar
          </Button>

          <Button
            onClick={handleEliminar}
            variant="contained"
            color="error"
            disabled={eliminando}
            sx={{ fontWeight: 'bold', textTransform: 'none' }}
          >
            Eliminar permanentemente
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={confirmado}
        autoHideDuration={4000}
        onClose={() => setConfirmado(false)}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert severity="success" variant="filled" sx={{ bgcolor: '#1976d2', color: 'white' }}>
          ¡Producto eliminado correctamente!
        </Alert>
      </Snackbar>
    </>
  );
}
