import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Alert,
  Stack,
} from '@mui/material';

export default function AdminEditarProducto() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [producto, setProducto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Campos del formulario
  const [nombre, setNombre] = useState('');
  const [categoria, setCategoria] = useState('');
  const [precio, setPrecio] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [imagen, setImagen] = useState('');
  const [descuento, setDescuento] = useState('');

  useEffect(() => {
    async function fetchProducto() {
      try {
        setLoading(true);
        const res = await fetch(`http://localhost:5000/api/productos/${id}`);
        if (!res.ok) throw new Error('No se pudo cargar el producto');
        const data = await res.json();
        setProducto(data);

        setNombre(data.nombre || '');
        setCategoria(data.categoria || '');
        setPrecio(data.precio?.toString() || '');
        setDescripcion(data.descripcion || '');
        setImagen(data.imagen || '');
        setDescuento(data.descuento?.toString() || '');
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchProducto();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!nombre.trim() || !categoria.trim() || !precio) {
      setError('Por favor completa todos los campos obligatorios.');
      return;
    }

    if (isNaN(precio) || parseFloat(precio) < 0) {
      setError('El precio debe ser un número positivo.');
      return;
    }

    if (descuento && (isNaN(descuento) || descuento < 0 || descuento > 100)) {
      setError('El descuento debe ser un número entre 0 y 100.');
      return;
    }

    const updatedProduct = {
      nombre,
      categoria,
      precio: parseFloat(precio),
      descripcion,
      imagen,
      descuento: descuento ? parseFloat(descuento) : 0,
    };

    try {
      const res = await fetch(`http://localhost:5000/api/productos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedProduct),
      });

      if (!res.ok) throw new Error('Error al actualizar el producto');

      alert('Producto actualizado con éxito');
      navigate('/admin/productos');
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) {
    return (
      <Box className="admin-loading">
        <CircularProgress color="success" size={60} />
      </Box>
    );
  }

  return (
    <Box className="admin-layout">
      <aside className="admin-sidebar">
        <Typography variant="h5" className="admin-sidebar-title">Admin Panel</Typography>
        <nav className="admin-nav">
          <Button onClick={() => navigate('/admin/productos')} fullWidth>
            Productos
          </Button>
          <Button onClick={() => navigate('/admin/usuarios')} fullWidth>
            Usuarios
          </Button>
          <Button onClick={() => navigate('/admin/ordenes')} fullWidth>
            Órdenes
          </Button>
        </nav>
      </aside>

      <main className="admin-content">
        <Typography variant="h3" className="admin-title" gutterBottom>
          Editar Producto
        </Typography>

        {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

        <Box component="form" onSubmit={handleSubmit} className="admin-form">
          <Stack spacing={3}>
            <TextField
              label="Nombre del producto *"
              variant="outlined"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
              fullWidth
            />

            <TextField
              label="Categoría *"
              variant="outlined"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
              required
              fullWidth
              helperText="Ejemplo: Mascotas, Jardinería, Químicos"
            />

            <TextField
              label="Precio *"
              variant="outlined"
              type="number"
              value={precio}
              onChange={(e) => setPrecio(e.target.value)}
              required
              fullWidth
              inputProps={{ min: 0, step: '0.01' }}
            />

            <TextField
              label="Descripción"
              variant="outlined"
              multiline
              rows={4}
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              fullWidth
            />

            <TextField
              label="URL de la imagen"
              variant="outlined"
              value={imagen}
              onChange={(e) => setImagen(e.target.value)}
              fullWidth
              placeholder="https://"
            />

            <TextField
              label="Descuento (%)"
              variant="outlined"
              type="number"
              value={descuento}
              onChange={(e) => setDescuento(e.target.value)}
              fullWidth
              inputProps={{ min: 0, max: 100, step: 1 }}
              helperText="Valor entre 0 y 100"
            />

            <Stack direction="row" justifyContent="space-between" spacing={2} sx={{ mt: 2 }}>
              <Button variant="outlined" color="error" onClick={() => navigate(-1)} sx={{ flex: 1 }}>
                Cancelar
              </Button>
              <Button type="submit" variant="contained" color="success" sx={{ flex: 1 }}>
                Guardar Cambios
              </Button>
            </Stack>
          </Stack>
        </Box>
      </main>
    </Box>
  );
}
