import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  IconButton,
  InputAdornment,
  Snackbar,
  Alert
} from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';

export default function LoginForm({ onClose, onLoginSuccess }) {
  const [mostrarClave, setMostrarClave] = React.useState(false);
  const [errorLogin, setErrorLogin] = React.useState('');
  const [camposVacios, setCamposVacios] = React.useState({ email: false, password: false });

  const onSubmit = (data) => {
    const email = data.email;
    const password = data.password;

    const nuevosErrores = {
      email: !email,
      password: !password
    };
    setCamposVacios(nuevosErrores);
    if (nuevosErrores.email || nuevosErrores.password) return;

    // Simulación de login
    if (email === 'admin@admin.com' && password === 'admin123') {
      localStorage.setItem('token', 'admin-token');
      localStorage.setItem('token-email', email);
      localStorage.setItem('rol', 'admin');
      if (onLoginSuccess) onLoginSuccess();
      if (onClose) onClose();
      window.dispatchEvent(new Event('storage'));
    } else if (email === 'demo@usuario.com' && password === '1234') {
      localStorage.setItem('token', 'usuario-token');
      localStorage.setItem('token-email', email);
      localStorage.setItem('rol', 'usuario');
      if (onLoginSuccess) onLoginSuccess();
      if (onClose) onClose();
      window.dispatchEvent(new Event('storage'));
    } else {
      setErrorLogin('Usuario o contraseña incorrectos');
    }
  };

  return (
    <Box
      component="form"
      onSubmit={(e) => {
        e.preventDefault();
        onSubmit({
          email: e.target.email?.value.trim(),
          password: e.target.password?.value.trim()
        });
      }}
      noValidate
      sx={{
        display: 'flex',
        flexDirection: 'column',
        gap: 3,
        maxWidth: 400,
        mx: 'auto',
        bgcolor: 'white',
        p: 4,
        borderRadius: 4,
        boxShadow: 3
      }}
    >
      <Typography variant="h5" sx={{ fontWeight: 'bold', textAlign: 'center', color: '#2e7d32' }}>
        Iniciar sesión
      </Typography>

      <TextField
        name="email"
        label="Correo electrónico"
        type="email"
        fullWidth
        autoFocus
        autoComplete="username"
        variant="outlined"
        error={camposVacios.email}
        helperText={camposVacios.email ? 'Este campo es obligatorio' : ''}
      />

      <TextField
        name="password"
        label="Contraseña"
        type={mostrarClave ? 'text' : 'password'}
        fullWidth
        autoComplete="current-password"
        variant="outlined"
        error={camposVacios.password}
        helperText={camposVacios.password ? 'Este campo es obligatorio' : ''}
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton onClick={() => setMostrarClave(!mostrarClave)}>
                {mostrarClave ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          )
        }}
      />

      <Button
        type="submit"
        variant="contained"
        fullWidth
        sx={{
          bgcolor: '#388e3c',
          color: 'white',
          fontWeight: 'bold',
          '&:hover': { bgcolor: '#2e7d32' }
        }}
      >
        Iniciar sesión
      </Button>

      <Typography variant="body2" align="center" sx={{ mt: 1 }}>
        ¿No te has registrado?{' '}
        <a href="/registro" style={{ fontWeight: 'bold', color: '#388e3c', textDecoration: 'underline' }}>
          Regístrate aquí
        </a>
      </Typography>

      <Snackbar
        open={!!errorLogin}
        autoHideDuration={4000}
        onClose={() => setErrorLogin('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert severity="error" variant="filled" onClose={() => setErrorLogin('')}>
          {errorLogin}
        </Alert>
      </Snackbar>
    </Box>
  );
}
