import React from 'react';
import { useParams } from 'react-router-dom';
import {
  Box, Typography, Button, Chip,
  Breadcrumbs, Link, Stack
} from '@mui/material';
import { useAccessibility } from '../../context/AccessibilityContext';
import productos from '../../data/productos.json';

export default function ProductoDetalle() {
  const { id } = useParams();
  const { altoContraste } = useAccessibility();

  const producto = productos.find(p => String(p.id) === id);

  if (!producto) {
    return (
      <Box sx={{ mt: 8, px: 4, py: 6 }}>
        <Typography variant="h6" color="error">Producto no encontrado</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{
      mt: 8,
      px: 4,
      py: 6,
      maxWidth: '860px',
      mx: 'auto',
      backgroundColor: altoContraste ? '#0a0a0a' : '#fff',
      color: altoContraste ? '#fff' : '#000',
      borderRadius: 4,
      boxShadow: altoContraste
        ? '0 0 0 2px #ffd900'
        : '0 6px 20px rgba(0,0,0,0.1)'
    }}>

      {/* Navegación */}
      <Breadcrumbs separator="›" sx={{ mb: 4 }}>
        <Link href="/" color={altoContraste ? '#ffd900' : '#2e7d32'}>Inicio</Link>
        <Link href={`/productos/${producto.categoria.toLowerCase()}`} color={altoContraste ? '#ffd900' : '#2e7d32'}>
          {producto.categoria}
        </Link>
        <Typography>{producto.nombre}</Typography>
      </Breadcrumbs>

      {/* Título y etiqueta */}
      <Typography variant="h2" sx={{ fontWeight: 'bold', mb: 1 }}>
        {producto.nombre}
      </Typography>
      <Typography variant="subtitle1" sx={{ mb: 2, color: altoContraste ? '#ffd900' : '#2e7d32' }}>
        {producto.categoria}
      </Typography>
      {producto.destacado && (
        <Chip label={producto.destacado} color="success" sx={{ mb: 2 }} />
      )}

      {/* Imagen institucional */}
      <Box sx={{ mb: 4 }}>
        <img
          src={producto.imagen}
          alt={producto.nombre}
          style={{
            width: '100%',
            borderRadius: 16,
            boxShadow: altoContraste
              ? '0 0 0 2px #ffd900'
              : '0 4px 14px rgba(0,0,0,0.1)'
          }}
        />
      </Box>

      {/* Descripción técnica */}
      <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.8 }}>
        {producto.descripcion}
      </Typography>

      {/* Botones institucionales */}
      <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
        {producto.botones?.map((btn, index) => (
          <Button
            key={index}
            variant="contained"
            href={btn.url}
            target="_blank"
            sx={{
              bgcolor: altoContraste ? '#ffd900' : '#2e7d32',
              color: altoContraste ? '#000' : '#fff',
              fontWeight: 'bold',
              px: 3,
              '&:hover': {
                bgcolor: altoContraste ? '#ffeb3b' : '#1b5e20'
              }
            }}
          >
            {btn.label}
          </Button>
        ))}
      </Stack>
    </Box>
  );
}
