import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  IconButton,
  InputAdornment,
  Snackbar,
  Alert
} from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';

export default function LoginForm({ onClose, onLoginSuccess }) {
  const [mostrarClave, setMostrarClave] = React.useState(false);
  const [errorLogin, setErrorLogin] = React.useState('');

  const onSubmit = (data) => {
    if (data.email === 'demo@usuario.com' && data.password === '1234') {
      localStorage.setItem('token', 'demo-token');
      localStorage.setItem('token-email', data.email);
      if (onLoginSuccess) onLoginSuccess();
      if (onClose) onClose();
      window.dispatchEvent(new Event('storage'));
    } else {
      setErrorLogin('Usuario o contraseña incorrectos');
    }
  };

  return (
    <Box
      component="form"
      onSubmit={(e) => {
        e.preventDefault();
        onSubmit({
          email: e.target.email?.value,
          password: e.target.password?.value
        });
      }}
      noValidate
      sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}
    >
      <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
        Iniciar sesión
      </Typography>

      <TextField
        name="email"
        label="Correo electrónico"
        type="email"
        fullWidth
        autoFocus
        autoComplete="username"
      />

      <TextField
        name="password"
        label="Contraseña"
        type={mostrarClave ? 'text' : 'password'}
        fullWidth
        autoComplete="current-password"
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton
                onClick={() => setMostrarClave(!mostrarClave)}
                aria-label={mostrarClave ? 'Ocultar contraseña' : 'Mostrar contraseña'}
              >
                {mostrarClave ? <VisibilityOffIcon /> : <VisibilityIcon />}
              </IconButton>
            </InputAdornment>
          )
        }}
      />

      <Button type="submit" variant="contained" color="primary" fullWidth>
        Iniciar sesión
      </Button>

      <Typography variant="body2" align="center" sx={{ mt: 2 }}>
        ¿No te has registrado?{' '}
        <a href="/registro" style={{ fontWeight: 'bold', color: '#388e3c' }}>
          Regístrate aquí
        </a>
      </Typography>

      <Snackbar
        open={!!errorLogin}
        autoHideDuration={4000}
        onClose={() => setErrorLogin('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert severity="error" variant="filled" onClose={() => setErrorLogin('')}>
          {errorLogin}
        </Alert>
      </Snackbar>
    </Box>
  );
}
