import React, { useState } from 'react';
import {
  Box,
  Fab,
  Drawer,
  Typography,
  Grid,
  IconButton,
  Button
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ContrastIcon from '@mui/icons-material/Tonality';
import PsychologyIcon from '@mui/icons-material/Psychology';
import ImageIcon from '@mui/icons-material/Image';
import AnimationIcon from '@mui/icons-material/MotionPhotosOff';
import LinkIcon from '@mui/icons-material/Link';
import MouseIcon from '@mui/icons-material/Mouse';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import InvertColorsIcon from '@mui/icons-material/InvertColors';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import { useAccessibility } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);

  const {
    panelAbierto,
    setPanelAbierto,
    tamañoTexto,
    espaciadoTexto,
    setTamañoTexto,
    setEspaciadoTexto,
    toggleContraste,
    toggleDislexia,
    toggleOcultarImágenes,
    toggleResaltarEnlaces,
    toggleAnimaciones,
    toggleModoLupa,
    toggleMonocromatico,
    leerTexto
  } = useAccessibility();

  const handleOpen = () => {
    setOpen(true);
    setPanelAbierto(true);
  };

  const handleClose = () => {
    setOpen(false);
    setPanelAbierto(false);
  };

  const resetAccesibilidad = () => {
    setTamañoTexto(1);
    setEspaciadoTexto(1);
    toggleContraste(false);
    toggleDislexia(false);
    toggleOcultarImágenes(false);
    toggleResaltarEnlaces(false);
    toggleAnimaciones(false);
    toggleModoLupa(false);
    toggleMonocromatico(false);
  };

  return (
    <>
      <Fab
        onClick={handleOpen}
        sx={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          backgroundColor: '#000',
          color: '#fff',
          zIndex: 1300,
          '&:hover': {
            backgroundColor: '#333'
          }
        }}
        aria-label="Accesibilidad"
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>

      <Drawer
        anchor="right"
        open={open}
        onClose={handleClose}
        PaperProps={{
          sx: {
            width: 360,
            p: 2,
            backgroundColor: 'rgba(255,255,255,0.95)',
            borderLeft: '1px solid #ddd',
            display: 'flex',
            flexDirection: 'column',
            height: '100vh'
          }
        }}
      >
        <Box sx={{ flexGrow: 1, overflowY: 'auto' }}>
          {/* 🌐 Selector de idioma */}
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 1, mb: 2 }}>
            <Button variant="outlined" size="small">🇪🇸</Button>
            <Button variant="outlined" size="small">🇺🇸</Button>
          </Box>

          <Typography variant="h6" gutterBottom>
            Menú de Accesibilidad
          </Typography>

          {/* 🔠 Tamaño de texto */}
          <Box sx={{ mb: 3 }}>
            <Typography variant="body2" sx={{ mb: 1, fontWeight: 'bold' }}>
              Tamaño de texto
            </Typography>
            <Box sx={{ display: 'flex', gap: 1 }}>
              {[1, 1.2, 1.4].map((valor) => (
                <Button
                  key={valor}
                  onClick={() => setTamañoTexto(valor)}
                  variant={tamañoTexto === valor ? 'contained' : 'outlined'}
                  sx={{
                    width: 32,
                    height: 32,
                    borderRadius: '50%',
                    fontSize: '0.75rem',
                    minWidth: 0
                  }}
                >
                  A{valor > 1 ? '+' : ''}
                </Button>
              ))}
            </Box>
          </Box>

          {/* 🔡 Espaciado de texto */}
          <Box sx={{ mb: 3 }}>
            <Typography variant="body2" sx={{ mb: 1, fontWeight: 'bold' }}>
              Espaciado de texto
            </Typography>
            <Box sx={{ display: 'flex', gap: 1 }}>
              {[1, 1.3, 1.6].map((valor) => (
                <Button
                  key={valor}
                  onClick={() => setEspaciadoTexto(valor)}
                  variant={espaciadoTexto === valor ? 'contained' : 'outlined'}
                  sx={{
                    width: 32,
                    height: 32,
                    borderRadius: '50%',
                    fontSize: '0.75rem',
                    minWidth: 0
                  }}
                >
                  {valor}
                </Button>
              ))}
            </Box>
          </Box>

          {/* 🧩 Controles individuales */}
          <Grid container spacing={2}>
            {[
              { icon: <InvertColorsIcon />, label: 'Invertir colores', onClick: toggleMonocromatico },
              { icon: <VolumeUpIcon />, label: 'Leer pantalla', onClick: () => leerTexto(document.body.innerText.slice(0, 500)) },
              { icon: <ContrastIcon />, label: 'Alto contraste', onClick: toggleContraste },
              { icon: <LinkIcon />, label: 'Resaltar enlaces', onClick: toggleResaltarEnlaces },
              { icon: <AnimationIcon />, label: 'Detener animaciones', onClick: toggleAnimaciones },
              { icon: <ImageIcon />, label: 'Ocultar imágenes', onClick: toggleOcultarImágenes },
              { icon: <PsychologyIcon />, label: 'Dislexia', onClick: toggleDislexia },
              { icon: <MouseIcon />, label: 'Modo lupa', onClick: toggleModoLupa }
            ].map((btn, index) => (
              <Grid item xs={6} key={index}>
                <Box
                  onClick={btn.onClick}
                  sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    p: 2,
                    backgroundColor: '#f5f5f5',
                    borderRadius: 2,
                    border: '1px solid #ddd',
                    cursor: 'pointer',
                    transition: '0.3s',
                    '&:hover': {
                      backgroundColor: '#e0f2f1',
                      borderColor: '#26a69a'
                    }
                  }}
                >
                  <IconButton color="primary" size="large">{btn.icon}</IconButton>
                  <Typography variant="caption" align="center">
                    {btn.label}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>

        {/* 🔄 Botón de reset abajo */}
        <Box sx={{ mt: 2 }}>
          <Button
            onClick={resetAccesibilidad}
            variant="outlined"
            fullWidth
            sx={{ color: '#d32f2f', borderColor: '#d32f2f' }}
          >
            Restablecer ajustes
          </Button>
        </Box>
      </Drawer>
    </>
  );
}
