'use client';

import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  MenuItem,
  InputLabel,
  FormControl,
  Select,
  OutlinedInput
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import { styled } from '@mui/system';

const tiposServicio = [
  'Cotización agrícola',
  'Consulta veterinaria',
  'Laboratorio técnico',
  'Soporte rural',
  'Rastreo de pedido'
];

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  borderRadius: 20,
  maxWidth: 650,
  margin: 'auto',
  background: 'linear-gradient(145deg, #f0fff4, #e0f7fa)',
  boxShadow: '0 8px 20px rgba(0,0,0,0.1)',
}));

const StyledButton = styled(Button)(({ theme }) => ({
  backgroundColor: '#00c853',
  color: 'white',
  fontWeight: 'bold',
  textTransform: 'none',
  padding: '12px 20px',
  fontSize: '16px',
  borderRadius: '12px',
  '&:hover': {
    backgroundColor: '#00b248',
  }
}));

export default function FormularioServicio() {
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    tipo: '',
    mensaje: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Solicitud enviada:', formData);
    alert('✅ Solicitud enviada correctamente (modo local)');
  };

  return (
    <Box sx={{ pt: 10, px: { xs: 2, md: 4 }, pb: 6, background: '#f5f5f5', minHeight: '100vh' }}>
      <Typography
        variant="h4"
        sx={{
          fontWeight: 'bold',
          textAlign: 'center',
          color: '#00796b',
          mb: 4,
          textShadow: '1px 1px 1px #b2dfdb'
        }}
      >
        ✍️ Solicita tu Servicio en Línea
      </Typography>

      <StyledPaper>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Nombre completo"
            name="nombre"
            fullWidth
            required
            value={formData.nombre}
            onChange={handleChange}
            variant="outlined"
            sx={{ mb: 3 }}
          />
          <TextField
            label="Correo electrónico"
            name="email"
            type="email"
            fullWidth
            required
            value={formData.email}
            onChange={handleChange}
            variant="outlined"
            sx={{ mb: 3 }}
          />
          <FormControl fullWidth required sx={{ mb: 3 }}>
            <InputLabel id="tipo-label">Tipo de servicio</InputLabel>
            <Select
              labelId="tipo-label"
              name="tipo"
              value={formData.tipo}
              onChange={handleChange}
              input={<OutlinedInput label="Tipo de servicio" />}
            >
              {tiposServicio.map((tipo, idx) => (
                <MenuItem key={idx} value={tipo}>
                  {tipo}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            label="Mensaje o detalle del servicio"
            name="mensaje"
            multiline
            rows={4}
            fullWidth
            required
            value={formData.mensaje}
            onChange={handleChange}
            variant="outlined"
            sx={{ mb: 3 }}
          />
          <StyledButton
            type="submit"
            fullWidth
            endIcon={<SendIcon />}
          >
            Enviar Solicitud
          </StyledButton>
        </form>
      </StyledPaper>
    </Box>
  );
}
