import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  IconButton
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

export default function RegistroVerificacionModal({ onClose, onVerificar }) {
  const [identificacion, setIdentificacion] = useState('');
  const [error, setError] = useState('');

  const handleVerificar = () => {
    if (!identificacion.trim()) {
      setError('Este campo es obligatorio');
      return;
    }

    // Podés agregar validaciones adicionales aquí
    setError('');
    onVerificar(identificacion); // ⬅️ Lanza siguiente paso
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        bgcolor: 'rgba(0,0,0,0.5)',
        zIndex: 1200,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
      }}
    >
      <Box
        sx={{
          position: 'relative',
          width: { xs: '90%', sm: 420 },
          bgcolor: 'white',
          borderRadius: 3,
          boxShadow: 6,
          px: 4,
          pt: 4,
          pb: 3
        }}
      >
        {/* Botón cerrar */}
        <IconButton onClick={onClose} sx={{ position: 'absolute', top: 12, right: 12 }}>
          <CloseIcon />
        </IconButton>

        {/* Título */}
        <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#2e7d32', mb: 3 }}>
          Verificación de identidad
        </Typography>

        {/* Campo identificación */}
        <TextField
          label="Número de identificación"
          fullWidth
          value={identificacion}
          onChange={(e) => setIdentificacion(e.target.value)}
          error={Boolean(error)}
          helperText={error}
          placeholder="Ej. 0912345678"
          sx={{ mb: 3 }}
        />

        {/* Botón verificar */}
        <Button
          fullWidth
          variant="contained"
          onClick={handleVerificar}
          sx={{
            bgcolor: '#fb8c00',
            color: 'white',
            fontWeight: 'bold',
            textTransform: 'none',
            py: 1.2,
            fontSize: '0.95rem',
            borderRadius: 2,
            '&:hover': { bgcolor: '#f57c00' }
          }}
        >
          Verificar
        </Button>
      </Box>
    </Box>
  );
}
