import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  IconButton,
  InputBase,
  Menu,
  MenuItem
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { Link, useNavigate } from 'react-router-dom';

export default function Navbar() {
  const navigate = useNavigate();

  const [anchorElPais, setAnchorElPais] = useState(null);
  const [paisSeleccionado, setPaisSeleccionado] = useState("Ecuador");

  const handleOpenPais = (event) => setAnchorElPais(event.currentTarget);
  const handleClosePais = () => setAnchorElPais(null);
  const cambiarPais = (pais) => {
    setPaisSeleccionado(pais);
    handleClosePais();
  };

  return (
    <AppBar position="static" sx={{ bgcolor: '#2e7d32' }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
        {/* Logo y t√≠tulo */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <IconButton edge="start" color="inherit" aria-label="men√∫">
            <MenuIcon />
          </IconButton>
          <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
            <Typography variant="h6" component="div" sx={{ fontWeight: 'bold' }}>
              üåæ Mercado Agr√≠cola
            </Typography>
          </Link>
        </Box>

        {/* Botones estilo institucional tipo Agripac */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 3 }}>
          <Button color="inherit" onClick={() => navigate('/nosotros')}>Nosotros</Button>
          <Button color="inherit" onClick={() => navigate('/productos')}>Productos</Button>
          <Button color="inherit" onClick={() => navigate('/servicios')}>Servicios en l√≠nea</Button>

          {/* Selector de pa√≠s */}
          <Button color="inherit" onClick={handleOpenPais} endIcon={<ExpandMoreIcon />}>
            Pa√≠s: {paisSeleccionado}
          </Button>
          <Menu anchorEl={anchorElPais} open={Boolean(anchorElPais)} onClose={handleClosePais}>
            <MenuItem onClick={() => cambiarPais('Ecuador')}> Ecuador</MenuItem>
            <MenuItem onClick={() => cambiarPais('Per√∫')}> Per√∫</MenuItem>
            <MenuItem onClick={() => cambiarPais('Colombia')}> Colombia</MenuItem>
            <MenuItem onClick={() => cambiarPais('M√©xico')}> M√©xico</MenuItem>
          </Menu>

          {/* Barra de b√∫squeda visual */}
          <Box sx={{
            display: 'flex',
            alignItems: 'center',
            backgroundColor: '#ffffff33',
            padding: '4px 12px',
            borderRadius: '20px',
            transition: 'all 0.3s ease-in-out',
            '&:hover': { backgroundColor: '#ffffff55' }
          }}>
            <InputBase
              placeholder="Buscar‚Ä¶"
              sx={{ color: 'inherit', ml: 1 }}
            />
            <IconButton size="small" sx={{ color: 'inherit' }}>
              <SearchIcon />
            </IconButton>
          </Box>
        </Box>
      </Toolbar>
    </AppBar>
  );
}
