import React, { useState, useContext, useEffect } from 'react';
import {
  Box, Fab, Popper, Paper, Button, Typography, Fade
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [sobreBanner, setSobreBanner] = useState(true);

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto
  } = useContext(AccessibilityContext);

  // Detectar si el scroll está sobre el carrusel (ej. < 600px)
  useEffect(() => {
    const handleScroll = () => {
      setSobreBanner(window.scrollY < 600);
    };
    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Inicializar al cargar
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
    setOpen((prev) => !prev);
  };

  // Estilo del botón según la posición en scroll
  const estiloBoton = sobreBanner
    ? {
        backgroundColor: '#1976d2',
        color: '#fff',
        boxShadow: '0 8px 24px rgba(0,0,0,0.4)',
        width: 64,
        height: 64,
        borderRadius: '16px',
        '&:hover': {
          backgroundColor: '#115293',
          transform: 'scale(1.1)',
          boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
        },
        transition: 'all 0.3s ease-in-out'
      }
    : {
        backgroundColor: '#ffffffee',
        color: '#1976d2',
        border: '2px solid #1976d2',
        boxShadow: '0 8px 24px rgba(0,0,0,0.35)',
        width: 64,
        height: 64,
        borderRadius: '16px',
        backdropFilter: 'blur(6px)',
        '&:hover': {
          backgroundColor: '#ffffff',
          transform: 'scale(1.1)',
          boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
        },
        transition: 'all 0.3s ease-in-out'
      };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: 24,
        right: 24,
        zIndex: 1300
      }}
    >
      <Fab
        onClick={handleClick}
        aria-label="Accesibilidad"
        sx={estiloBoton}
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>

      <Popper open={open} anchorEl={anchorEl} transition placement="top-end">
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={250}>
            <Paper
              elevation={10}
              sx={{
                p: 3,
                mt: 1.5,
                minWidth: 260,
                backgroundColor: '#fdfdfd',
                borderRadius: '16px',
                boxShadow: '0 8px 24px rgba(0,0,0,0.2)',
                border: '1px solid #e0e0e0'
              }}
            >
              <Typography
                variant="h6"
                sx={{ fontWeight: 700, color: '#1976d2', mb: 2 }}
              >
                ⚙️ Opciones de accesibilidad
              </Typography>

              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1.2 }}>
                <Button
                  variant="contained"
                  color={altoContraste ? 'secondary' : 'primary'}
                  fullWidth
                  onClick={toggleContraste}
                  sx={{ textTransform: 'none', fontWeight: 500 }}
                >
                  {altoContraste ? 'Desactivar contraste alto' : 'Activar contraste alto'}
                </Button>

                <Button
                  variant="contained"
                  color={modoDislexia ? 'secondary' : 'primary'}
                  fullWidth
                  onClick={toggleDislexia}
                  sx={{ textTransform: 'none', fontWeight: 500 }}
                >
                  {modoDislexia ? 'Desactivar modo dislexia' : 'Activar modo dislexia'}
                </Button>

                <Box sx={{ mt: 1 }}>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>
                    Escala de texto: <strong>{Math.round(tamañoTexto * 100)}%</strong>
                  </Typography>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Button
                      onClick={disminuirTexto}
                      variant="outlined"
                      size="small"
                      sx={{ minWidth: 40 }}
                    >
                      –
                    </Button>
                    <Button
                      onClick={aumentarTexto}
                      variant="outlined"
                      size="small"
                      sx={{ minWidth: 40 }}
                    >
                      +
                    </Button>
                  </Box>
                </Box>
              </Box>
            </Paper>
          </Fade>
        )}
      </Popper>
    </Box>
  );
}
