import React from 'react';
import {
  Modal,
  Box,
  IconButton,
  Button,
  Typography,
  Avatar,
  Snackbar,
  Alert
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import LoginForm from '../forms/LoginForm';

export default function LoginModal({ onClose }) {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [emailActual, setEmailActual] = React.useState(localStorage.getItem('token-email'));
  const [logoutConfirmado, setLogoutConfirmado] = React.useState(false);

  React.useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem('token');
      const email = localStorage.getItem('token-email');
      setIsLoggedIn(!!token);
      setEmailActual(email);
    };

    window.addEventListener('storage', checkToken);
    checkToken(); // evaluar al montar

    return () => window.removeEventListener('storage', checkToken);
  }, []);

  const handleLogout = () => {
    setLogoutConfirmado(true); // Mostrar snackbar primero

    setTimeout(() => {
      localStorage.removeItem('token');
      localStorage.removeItem('token-email');
      setIsLoggedIn(false);
      setEmailActual(null);
      if (onClose) onClose(); // Cerrar modal después del mensaje
    }, 2500);
  };

  return (
    <Modal
      open
      onClose={onClose}
      aria-labelledby="modal-login"
      aria-describedby="login-accessible"
    >
      <Box
        role="dialog"
        aria-modal="true"
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          bgcolor: 'white',
          p: 4,
          borderRadius: 3,
          width: { xs: '90%', sm: 420 },
          boxShadow: 24,
          outline: 0,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
          transition: 'all 0.3s ease',
          transformOrigin: 'center'
        }}
      >
        {/* Botón cerrar modal */}
        <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
          <IconButton onClick={onClose} aria-label="Cerrar login">
            <CloseIcon />
          </IconButton>
        </Box>

        {/* Formulario si no está logueado */}
        {!isLoggedIn && (
          <LoginForm
            onClose={onClose}
            onLoginSuccess={() => {
              setIsLoggedIn(true);
              setEmailActual(localStorage.getItem('token-email'));
            }}
          />
        )}

        {/* Sesión activa: bienvenida + botón */}
        {isLoggedIn && emailActual && (
          <>
            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 1 }}>
              <Avatar sx={{ bgcolor: '#388e3c', color: 'white' }}>
                {emailActual.charAt(0).toUpperCase()}
              </Avatar>
              <Typography variant="body1" align="center">
                Sesión iniciada como <strong>{emailActual}</strong>
              </Typography>
            </Box>

            <Button
              variant="outlined"
              color="error"
              fullWidth
              onClick={handleLogout}
            >
              Cerrar sesión
            </Button>
          </>
        )}

        {/* Snackbar tras cerrar sesión */}
        <Snackbar
          open={logoutConfirmado}
          autoHideDuration={4000}
          onClose={() => setLogoutConfirmado(false)}
          anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
        >
          <Alert
            severity="info"
            variant="filled"
            onClose={() => setLogoutConfirmado(false)}
            sx={{ bgcolor: '#1976d2', color: 'white' }}
          >
            Hasta luego, {emailActual}. Sesión cerrada correctamente.
          </Alert>
        </Snackbar>
      </Box>
    </Modal>
  );
}
