import React from 'react';
import {
  Container, Box, Typography, TextField, Button, MenuItem, FormControl, InputLabel, Select
} from '@mui/material';
import { useForm } from 'react-hook-form';

export default function ProductoForm() {
  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors }
  } = useForm();

  const onSubmit = (data) => {
    console.log('Producto registrado:', data);
    // Aquí podrías hacer una petición POST con Axios al backend
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 5 }}>
        <Typography variant="h4" gutterBottom>
          Guardar Producto Agrícola
        </Typography>

        <form onSubmit={handleSubmit(onSubmit)} noValidate aria-label="Formulario de productos">

          {/* Categoría */}
          <FormControl fullWidth margin="normal">
            <InputLabel id="categoria-label">Categoría</InputLabel>
            <Select
              labelId="categoria-label"
              defaultValue=""
              {...register('categoria', { required: 'Debes seleccionar una categoría' })}
              onChange={(e) => setValue('categoria', e.target.value)}
              error={!!errors.categoria}
            >
              <MenuItem value="fruta">Fruta</MenuItem>
              <MenuItem value="verdura">Verdura</MenuItem>
              <MenuItem value="semilla">Semilla</MenuItem>
              <MenuItem value="herramienta">Herramienta</MenuItem>
              <MenuItem value="ganado">Ganado</MenuItem>
            </Select>
            {errors.categoria && (
              <Typography variant="caption" color="error">
                {errors.categoria.message}
              </Typography>
            )}
          </FormControl>

          {/* Nombre del producto */}
          <TextField
            label="Nombre del producto"
            fullWidth
            margin="normal"
            {...register('nombre', { required: 'Este campo es obligatorio' })}
            error={!!errors.nombre}
            helperText={errors.nombre?.message}
            inputProps={{ 'aria-label': 'Campo nombre producto' }}
          />

          {/* Descripción */}
          <TextField
            label="Descripción"
            fullWidth
            multiline
            rows={3}
            margin="normal"
            {...register('descripcion')}
            inputProps={{ 'aria-label': 'Campo descripción' }}
          />

          {/* Precio */}
          <TextField
            label="Precio (USD)"
            type="number"
            fullWidth
            margin="normal"
            {...register('precio', {
              required: 'El precio es obligatorio',
              min: { value: 0, message: 'El precio debe ser positivo' }
            })}
            error={!!errors.precio}
            helperText={errors.precio?.message}
            inputProps={{ 'aria-label': 'Campo precio' }}
          />

          {/* Cantidad */}
          <TextField
            label="Cantidad disponible"
            type="number"
            fullWidth
            margin="normal"
            {...register('cantidad', {
              required: 'La cantidad es obligatoria',
              min: { value: 0, message: 'Debe ser un número positivo' }
            })}
            error={!!errors.cantidad}
            helperText={errors.cantidad?.message}
            inputProps={{ 'aria-label': 'Campo cantidad disponible' }}
          />

          {/* Botón de guardar */}
          <Button type="submit" variant="contained" color="success" fullWidth sx={{ mt: 3 }}>
            Guardar producto
          </Button>
        </form>
      </Box>
    </Container>
  );
}
