import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
  Alert,
} from '@mui/material';

export default function EditarProductoForm() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [producto, setProducto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Campos del formulario
  const [nombre, setNombre] = useState('');
  const [categoria, setCategoria] = useState('');
  const [precio, setPrecio] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [imagen, setImagen] = useState('');
  const [descuento, setDescuento] = useState('');

  // Cargar producto por ID
  useEffect(() => {
    async function fetchProducto() {
      try {
        setLoading(true);
        const res = await fetch(`http://localhost:5000/api/productos/${id}`);
        if (!res.ok) throw new Error('No se pudo cargar el producto');
        const data = await res.json();
        setProducto(data);

        // Rellenar formulario
        setNombre(data.nombre || '');
        setCategoria(data.categoria || '');
        setPrecio(data.precio?.toString() || '');
        setDescripcion(data.descripcion || '');
        setImagen(data.imagen || '');
        setDescuento(data.descuento?.toString() || '');

      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchProducto();
  }, [id]);

  // Validar y enviar datos actualizados
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validación simple
    if (!nombre.trim() || !categoria.trim() || !precio) {
      setError('Por favor completa todos los campos obligatorios.');
      return;
    }

    const updatedProduct = {
      nombre,
      categoria,
      precio: parseFloat(precio),
      descripcion,
      imagen,
      descuento: descuento ? parseFloat(descuento) : 0,
    };

    try {
      const res = await fetch(`http://localhost:5000/api/productos/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedProduct),
      });

      if (!res.ok) throw new Error('Error al actualizar el producto');

      alert('Producto actualizado con éxito');
      navigate('/comprar'); // redirige a lista o donde prefieras

    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <Box sx={{ textAlign: 'center', mt: 10 }}><CircularProgress /></Box>;

  if (error) return <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>;

  return (
    <Box sx={{ maxWidth: 600, mx: 'auto', mt: 6, p: 3, boxShadow: 3, borderRadius: 2 }}>
      <Typography variant="h4" mb={3} sx={{ color: '#2e7d32' }}>
        Editar Producto
      </Typography>

      <form onSubmit={handleSubmit} noValidate>
        <TextField
          label="Nombre"
          value={nombre}
          onChange={e => setNombre(e.target.value)}
          fullWidth
          required
          margin="normal"
        />

        <TextField
          label="Categoría"
          value={categoria}
          onChange={e => setCategoria(e.target.value)}
          fullWidth
          required
          margin="normal"
          helperText="Ejemplo: Mascotas, Jardinería, Químicos"
        />

        <TextField
          label="Precio"
          type="number"
          value={precio}
          onChange={e => setPrecio(e.target.value)}
          fullWidth
          required
          margin="normal"
          inputProps={{ min: 0, step: '0.01' }}
        />

        <TextField
          label="Descripción"
          value={descripcion}
          onChange={e => setDescripcion(e.target.value)}
          fullWidth
          multiline
          rows={3}
          margin="normal"
        />

        <TextField
          label="URL de Imagen"
          value={imagen}
          onChange={e => setImagen(e.target.value)}
          fullWidth
          margin="normal"
        />

        <TextField
          label="Descuento (%)"
          type="number"
          value={descuento}
          onChange={e => setDescuento(e.target.value)}
          fullWidth
          margin="normal"
          inputProps={{ min: 0, max: 100, step: '1' }}
          helperText="Porcentaje de descuento, si aplica"
        />

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

        <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
          <Button variant="outlined" color="secondary" onClick={() => navigate(-1)}>
            Cancelar
          </Button>

          <Button type="submit" variant="contained" sx={{ bgcolor: '#2e7d32', '&:hover': { bgcolor: '#1b4d19' } }}>
            Guardar cambios
          </Button>
        </Box>
      </form>
    </Box>
  );
}
