import React, { useState } from 'react';
import {
  Box,
  Fab,
  Drawer,
  Typography,
  Grid,
  IconButton,
  Button
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ContrastIcon from '@mui/icons-material/Tonality';
import PsychologyIcon from '@mui/icons-material/Psychology';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import ImageIcon from '@mui/icons-material/Image';
import AnimationIcon from '@mui/icons-material/MotionPhotosOff';
import LinkIcon from '@mui/icons-material/Link';
import MouseIcon from '@mui/icons-material/Mouse';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import StopCircleIcon from '@mui/icons-material/StopCircle';
import InvertColorsIcon from '@mui/icons-material/InvertColors';
import CloseIcon from '@mui/icons-material/Close';
import ControlNivelVisual from './ControlNivelVisual';
import { useAccessibility } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);

  const {
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto,
    toggleOcultarIm√°genes,
    toggleResaltarEnlaces,
    toggleAnimaciones,
    toggleModoLupa,
    toggleInvertirColores,
    leerTexto,
    detenerLectura,
    espaciadoNivel,
    setEspaciadoNivel,
    alturaLinea,
    setAlturaLinea
  } = useAccessibility();

  const handleReset = () => {
    setEspaciadoNivel('normal');
    setAlturaLinea('normal');
    toggleContraste(false);
    toggleDislexia(false);
    toggleOcultarIm√°genes(false);
    toggleResaltarEnlaces(false);
    toggleAnimaciones(false);
    toggleModoLupa(false);
    toggleInvertirColores(false);
    detenerLectura();
  };

  return (
    <>
      <Fab
        onClick={() => setOpen(true)}
        sx={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          bgcolor: '#000',
          color: '#fff',
          zIndex: 1300,
          display: open ? 'none' : 'flex',
          '&:hover': { bgcolor: '#333' }
        }}
        aria-label="Accesibilidad"
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>

      <Drawer
        anchor="right"
        open={open}
        onClose={() => setOpen(false)}
        PaperProps={{
          sx: {
            width: 360,
            p: 2,
            backgroundColor: '#fff',
            height: '100vh',
            overflowY: 'auto',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between'
          }
        }}
      >
        <Box>
          {/* ‚ùå Bot√≥n de cerrar arriba */}
          <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 1 }}>
            <IconButton onClick={() => setOpen(false)} size="small">
              <CloseIcon />
            </IconButton>
          </Box>

          <Typography variant="h6" gutterBottom>
            Men√∫ de Accesibilidad
          </Typography>

          {/* üëÅÔ∏è Visual */}
          <Typography variant="subtitle2" sx={{ mt: 3, mb: 1 }}>
            üëÅÔ∏è Visual
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={6}><IconBox icon={<ContrastIcon />} label="Alto contraste" onClick={toggleContraste} /></Grid>
            <Grid item xs={6}><IconBox icon={<InvertColorsIcon />} label="Invertir colores" onClick={toggleInvertirColores} /></Grid>
            <Grid item xs={6}><IconBox icon={<ImageIcon />} label="Ocultar im√°genes" onClick={toggleOcultarIm√°genes} /></Grid>
            <Grid item xs={6}><IconBox icon={<LinkIcon />} label="Resaltar enlaces" onClick={toggleResaltarEnlaces} /></Grid>
          </Grid>

          {/* üî† Texto */}
          <Typography variant="subtitle2" sx={{ mt: 3, mb: 1 }}>
            üî† Texto
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={6}><IconBox icon={<TextFieldsIcon />} label="Aumentar texto" onClick={aumentarTexto} /></Grid>
            <Grid item xs={6}><IconBox icon={<TextFieldsIcon />} label="Reducir texto" onClick={disminuirTexto} /></Grid>
          </Grid>

          <ControlNivelVisual label="Espaciado de texto" state={espaciadoNivel} setState={setEspaciadoNivel} />
          <ControlNivelVisual label="Altura de l√≠nea" state={alturaLinea} setState={setAlturaLinea} />

          {/* üß† Cognitiva */}
          <Typography variant="subtitle2" sx={{ mt: 3, mb: 1 }}>
            üß† Cognitiva
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={6}><IconBox icon={<PsychologyIcon />} label="Tipograf√≠a dislexia" onClick={toggleDislexia} /></Grid>
            <Grid item xs={6}><IconBox icon={<AnimationIcon />} label="Detener animaciones" onClick={toggleAnimaciones} /></Grid>
            <Grid item xs={6}><IconBox icon={<VolumeUpIcon />} label="Leer pantalla" onClick={() => leerTexto(document.body.innerText.slice(0, 500))} /></Grid>
            <Grid item xs={6}><IconBox icon={<StopCircleIcon />} label="Detener lectura" onClick={detenerLectura} /></Grid>
          </Grid>

          {/* üß≠ Navegaci√≥n */}
          <Typography variant="subtitle2" sx={{ mt: 3, mb: 1 }}>
            üß≠ Navegaci√≥n
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={6}><IconBox icon={<MouseIcon />} label="Modo lupa" onClick={toggleModoLupa} /></Grid>
          </Grid>
        </Box>

        {/* üîÑ Reset */}
        <Box sx={{ mt: 2 }}>
          <Button
            variant="outlined"
            fullWidth
            onClick={handleReset}
            sx={{ color: '#d32f2f', borderColor: '#d32f2f' }}
          >
            Restablecer ajustes
          </Button>
        </Box>
      </Drawer>
    </>
  );
}

function IconBox({ icon, label, onClick }) {
  return (
    <Box
      onClick={onClick}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        p: 2,
        backgroundColor: '#f5f5f5',
        borderRadius: 2,
        border: '1px solid #ddd',
        cursor: 'pointer',
        transition: '0.3s',
        '&:hover': {
          backgroundColor: '#e0f2f1',
          borderColor: '#26a69a'
        }
      }}
    >
      <IconButton color="primary" size="large">{icon}</IconButton>
      <Typography variant="caption" align="center" sx={{ mt: 1 }}>{label}</Typography>
    </Box>
  );
}
