import React, { useState, useContext, useEffect } from 'react';
import {
  Box,
  Fab,
  Paper,
  Button,
  Typography,
  Collapse,
  Grid,
  ToggleButton,
  ToggleButtonGroup,
  Switch
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import ContrastIcon from '@mui/icons-material/Tonality';
import PsychologyIcon from '@mui/icons-material/Psychology';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import ImageIcon from '@mui/icons-material/Image';
import AnimationIcon from '@mui/icons-material/MotionPhotosOff';
import LinkIcon from '@mui/icons-material/Link';
import MouseIcon from '@mui/icons-material/Mouse';
import InvertColorsIcon from '@mui/icons-material/InvertColors';
import FormatLineSpacingIcon from '@mui/icons-material/FormatLineSpacing';
import SubtitlesIcon from '@mui/icons-material/Subtitles';
import HearingIcon from '@mui/icons-material/Hearing';
import RecordVoiceOverIcon from '@mui/icons-material/RecordVoiceOver';
import KeyboardIcon from '@mui/icons-material/Keyboard';
import AudiotrackIcon from '@mui/icons-material/Audiotrack';

import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [sobreBanner, setSobreBanner] = useState(true);

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    ocultarImágenes,
    resaltarEnlaces,
    detenerAnimaciones,
    modoLupa,
    invertirColores,
    espaciadoNivel,
    alturaLinea,
    lectorPantalla,
    descripcionAudio,
    subtitulosCerrados,
    subtitulosEnVivo,
    transcripcion,
    controlesAudio,
    alertasVisuales,
    skipLinkActivo,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto,
    toggleOcultarImágenes,
    toggleResaltarEnlaces,
    toggleAnimaciones,
    toggleModoLupa,
    toggleInvertirColores,
    setEspaciadoNivel,
    setAlturaLinea,
    setLectorPantalla,
    setDescripcionAudio,
    setSubtitulosCerrados,
    setSubtitulosEnVivo,
    setTranscripcion,
    setControlesAudio,
    setAlertasVisuales,
    setSkipLinkActivo
  } = useContext(AccessibilityContext);

  useEffect(() => {
    const handleScroll = () => setSobreBanner(window.scrollY < 600);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const tarjetaStyle = (activo) => ({
    p: 2,
    backgroundColor: activo ? '#e3f2fd' : '#f3f6f9',
    borderRadius: 3,
    border: `2px solid ${activo ? '#1976d2' : '#d0d7de'}`,
    boxShadow: activo
      ? '0 6px 20px rgba(25,118,210,0.25)'
      : '0 6px 18px rgba(0,0,0,0.08)',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
    textAlign: 'center',
    gap: 1,
    height: 190,
    transition: 'all 0.3s ease-in-out',
    '&:hover': {
      boxShadow: '0 10px 28px rgba(0,0,0,0.12)',
      transform: 'scale(1.02)'
    }
  });

  const toggleSwitch = (label, checked, onChange, icon) => (
    <Box sx={tarjetaStyle(checked)}>
      {icon}
      <Typography variant="subtitle2" sx={{ fontWeight: 600 }}>{label}</Typography>
      <Switch checked={checked} onChange={(e, v) => onChange(v)} />
    </Box>
  );

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: 24,
        right: 24,
        zIndex: 1300,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-end'
      }}
    >
      <Collapse in={open}>
        <Paper
          elevation={10}
          sx={{
            mb: 2,
            p: 3,
            borderRadius: '20px',
            backgroundColor: '#f3f6f9',
            boxShadow: '0 8px 32px rgba(0,0,0,0.15)',
            maxWidth: 400,
            maxHeight: '80vh',
            overflowY: 'auto'
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: 700, color: '#1976d2', mb: 2 }}>
            ⚙️ Opciones de accesibilidad
          </Typography>

          <Grid container spacing={2}>
            {/* Básicos */}
            <Grid item xs={6}>{toggleSwitch('Contraste', altoContraste, toggleContraste, <ContrastIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Monocromático', invertirColores, toggleInvertirColores, <InvertColorsIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Dislexia', modoDislexia, toggleDislexia, <PsychologyIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Lupa', modoLupa, toggleModoLupa, <MouseIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>

            {/* Tamaño texto */}
            <Grid item xs={12}>
              <Box sx={tarjetaStyle(false)}>
                <TextFieldsIcon fontSize="large" sx={{ color: '#1976d2' }} />
                <Typography variant="subtitle2" sx={{ fontWeight: 600 }}>Tamaño de texto</Typography>
                <Box sx={{ display: 'flex', gap: 1 }}>
                  <Button size="small" onClick={disminuirTexto}><RemoveIcon /></Button>
                  <Typography>{Math.round(tamañoTexto * 100)}%</Typography>
                  <Button size="small" onClick={aumentarTexto}><AddIcon /></Button>
                </Box>
              </Box>
            </Grid>

            {/* Espaciado y líneas */}
            <Grid item xs={6}>
              <Box sx={tarjetaStyle(true)}>
                <FormatLineSpacingIcon fontSize="large" sx={{ color: '#1976d2' }} />
                <Typography variant="subtitle2">Espaciado</Typography>
                <ToggleButtonGroup
                  size="small"
                  exclusive
                  value={espaciadoNivel}
                  onChange={(e, v) => v && setEspaciadoNivel(v)}
                >
                  <ToggleButton value="normal">1</ToggleButton>
                  <ToggleButton value="medio">2</ToggleButton>
                  <ToggleButton value="alto">3</ToggleButton>
                </ToggleButtonGroup>
              </Box>
            </Grid>

            <Grid item xs={6}>
              <Box sx={tarjetaStyle(true)}>
                <FormatLineSpacingIcon fontSize="large" sx={{ color: '#1976d2' }} />
                <Typography variant="subtitle2">Altura línea</Typography>
                <ToggleButtonGroup
                  size="small"
                  exclusive
                  value={alturaLinea}
                  onChange={(e, v) => v && setAlturaLinea(v)}
                >
                  <ToggleButton value="normal">1</ToggleButton>
                  <ToggleButton value="medio">2</ToggleButton>
                  <ToggleButton value="alto">3</ToggleButton>
                </ToggleButtonGroup>
              </Box>
            </Grid>

            {/* Otras opciones visuales */}
            <Grid item xs={6}>{toggleSwitch('Ocultar imágenes', ocultarImágenes, toggleOcultarImágenes, <ImageIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Resaltar enlaces', resaltarEnlaces, toggleResaltarEnlaces, <LinkIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Animaciones', detenerAnimaciones, toggleAnimaciones, <AnimationIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Navegación teclado', skipLinkActivo, (checked) => setSkipLinkActivo(checked), <KeyboardIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>

            {/* Auditivos */}
            <Grid item xs={6}>{toggleSwitch('Subtítulos', subtitulosCerrados, (checked) => setSubtitulosCerrados(checked), <SubtitlesIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Subtítulos en vivo', subtitulosEnVivo, (checked) => setSubtitulosEnVivo(checked), <HearingIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Transcripción', transcripcion, (checked) => setTranscripcion(checked), <RecordVoiceOverIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Controles de audio', controlesAudio, (checked) => setControlesAudio(checked), <AudiotrackIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Alertas visuales', alertasVisuales, (checked) => setAlertasVisuales(checked), <HearingIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={6}>{toggleSwitch('Lector pantalla', lectorPantalla, (checked) => setLectorPantalla(checked), <RecordVoiceOverIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
            <Grid item xs={12}>{toggleSwitch('Descripción de audio', descripcionAudio, (checked) => setDescripcionAudio(checked), <RecordVoiceOverIcon fontSize="large" sx={{ color: '#1976d2' }} />)}</Grid>
          </Grid>
        </Paper>
      </Collapse>

      <Fab
        onClick={() => setOpen((prev) => !prev)}
        aria-label="Accesibilidad"
        sx={{
          background: 'linear-gradient(135deg, #1976d2, #7e57c2)',
          color: '#fff',
          width: 64,
          height: 64,
          boxShadow: '0 8px 24px rgba(0,0,0,0.4)',
          '&:hover': {
            transform: 'scale(1.1)',
            boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
          },
          transition: 'all 0.3s ease-in-out'
        }}
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>
    </Box>
  );
}
