import React from 'react';
import {
  Modal,
  Box,
  IconButton,
  Fade,
  Backdrop
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import LoginForm from '../forms/LoginForm';

export default function LoginModal({ open, onClose }) {
  return (
    <Modal
      open={open}
      onClose={onClose}
      closeAfterTransition
      aria-labelledby="login-modal"
      aria-describedby="modal de acceso"
      BackdropComponent={Backdrop}
      BackdropProps={{
        timeout: 300,
        sx: {
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(6px)',
        }
      }}
    >
      <Fade in={open}>
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            bgcolor: 'white',
            borderRadius: 3,
            boxShadow: '0 10px 25px rgba(46, 125, 50, 0.3)',
            width: { xs: '90%', sm: 420 },
            p: 4,
            pt: 6,
            position: 'relative',
            display: 'flex',
            flexDirection: 'column',
            gap: 2,
            transition: 'all 0.3s ease',
            fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
          }}
        >
          {/* X de cierre flotante */}
          <IconButton
            onClick={onClose}
            sx={{
              position: 'absolute',
              top: 12,
              right: 12,
              color: '#388e3c',
              zIndex: 2
            }}
            aria-label="Cerrar modal"
          >
            <CloseIcon />
          </IconButton>

          {/* Formulario institucional limpio */}
          <LoginForm onLoginSuccess={onClose} />
        </Box>
      </Fade>
    </Modal>
  );
}
