import React from 'react';
import { Box, Typography } from '@mui/material';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function PantallaInicio() {
  const secciones = [
    {
      imagen: '/imagenes/carrusel1.jpg',
      titulo: 'Ganadería responsable para alimentar al mundo',
      subtitulo: 'Cuidamos cada paso del proceso productivo'
    },
    {
      imagen: '/imagenes/carrusel2.jpg',
      titulo: 'La naturaleza nos da vida, nosotros la protegemos',
      subtitulo: 'Cultivamos con respeto por la tierra'
    },
    {
      imagen: '/imagenes/carrusel3.jpg',
      titulo: 'Sembramos futuro con cada semilla',
      subtitulo: 'Innovación desde el inicio del ciclo productivo'
    },
    {
      imagen: '/imagenes/carrusel4.jpg',
      titulo: 'Frutas frescas, sabor natural directo del campo',
      subtitulo: 'Calidad agrícola para todos'
    }
  ];

  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: false,
    pauseOnHover: false
  };

  return (
    <Box sx={{ width: '100%', mt: 2 }}>
      <Slider {...settings}>
        {secciones.map((item, index) => (
          <Box
            key={index}
            sx={{
              position: 'relative',
              height: '60vh',
              backgroundImage: `url(${item.imagen})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              overflow: 'hidden'
            }}
          >
            {/* Capa de oscurecimiento */}
            <Box
              sx={{
                position: 'absolute',
                inset: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.35)',
                zIndex: 1
              }}
            />

            {/* Texto institucional en esquina */}
            <Box
              sx={{
                position: 'absolute',
                top: 40,
                left: 60,
                zIndex: 2,
                color: '#fff',
                maxWidth: '60%'
              }}
            >
              <Typography variant="h4" sx={{ fontWeight: 'bold', textShadow: '2px 2px 4px #000' }}>
                {item.titulo}
              </Typography>
              <Typography variant="h6" sx={{ mt: 1, textShadow: '1px 1px 3px #000' }}>
                {item.subtitulo}
              </Typography>
            </Box>
          </Box>
        ))}
      </Slider>
    </Box>
  );
}
