import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  Chip,
  TextField,
  InputAdornment
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import productos from '../../data/productos.json';

export default function Comprar() {
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('token'));

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      navigate('/login');
    } else {
      navigate(`/comprar/${id}`);
    }
  };

  return (
    <Box sx={{ pb: 6 }}>
      {/* Encabezado flotante institucional */}
      <Box sx={{ position: 'relative', zIndex: 10 }}>
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            px: 3,
            py: 2,
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            backgroundColor: 'rgba(255,255,255,0.95)',
            boxShadow: 1
          }}
        >
          {/* Branding */}
          <Typography
            variant="h6"
            component="a"
            href="/"
            sx={{
              textDecoration: 'none',
              color: '#2e7d32',
              fontWeight: 'bold',
              fontSize: '1.3rem',
              cursor: 'pointer',
              '&:hover': { textDecoration: 'underline' }
            }}
          >
            Mercado AgrÃ­cola
          </Typography>

          {/* Funciones: buscar, sesiÃ³n, carrito, agencia */}
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <TextField
              placeholder="Buscar productos..."
              size="small"
              sx={{
                bgcolor: '#f1f8e9',
                borderRadius: 20,
                width: { xs: '140px', md: '200px' }
              }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon sx={{ color: 'gray' }} />
                  </InputAdornment>
                )
              }}
            />
            <Button
              variant="outlined"
              href="/login"
              sx={{
                color: '#2e7d32',
                borderColor: '#2e7d32',
                fontWeight: 'bold',
                textTransform: 'uppercase',
                fontSize: '0.75rem'
              }}
            >
              INICIAR SESIÃN
            </Button>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, cursor: 'pointer' }}>
              <Typography sx={{ fontSize: '1.2rem' }}>ð</Typography>
              <Typography sx={{ fontWeight: 'bold', fontSize: '0.9rem' }}>$0.00</Typography>
            </Box>
            <Typography
              sx={{
                fontSize: '0.75rem',
                color: '#555',
                textDecoration: 'underline',
                cursor: 'pointer'
              }}
            >
              CAMBIAR AGENCIA
            </Typography>
          </Box>
        </Box>
      </Box>

      {/* Banner institucional */}
      <Box
        sx={{
          width: '100%',
          height: { xs: 180, md: 240 },
          backgroundImage: 'url("/imagenes/banner-pago.webp")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          position: 'relative',
          mt: 8
        }}
      >
        <Typography
          variant="h6"
          sx={{
            position: 'absolute',
            bottom: 12,
            left: 24,
            backgroundColor: 'rgba(0,0,0,0.5)',
            color: '#fff',
            px: 2,
            py: 1,
            borderRadius: 2,
            fontWeight: 'bold'
          }}
        >
          Â¿Transferencia o Tarjeta? Â¡TÃº eliges!
        </Typography>
      </Box>

      {/* SecciÃ³n de categorÃ­as */}
      <Box sx={{ px: { xs: 2, md: 4 }, py: 4 }}>
        <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 3 }}>
          CategorÃ­as destacadas
        </Typography>
        <Grid container spacing={3}>
          {['Perro', 'Gato', 'Limpieza y Aseo', 'JardinerÃ­a'].map((cat, idx) => (
            <Grid item xs={6} sm={3} key={idx}>
              <Paper
                elevation={3}
                sx={{
                  p: 2,
                  textAlign: 'center',
                  borderRadius: 3,
                  cursor: 'pointer',
                  '&:hover': { boxShadow: 4 }
                }}
              >
                <img
                  src={`/imagenes/categorias/${cat.toLowerCase().replace(/ /g, '-')}.webp`}
                  alt={cat}
                  style={{ width: '100%', borderRadius: 8 }}
                />
                <Typography sx={{ mt: 1, fontWeight: 'bold' }}>{cat}</Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* Productos destacados */}
      <Box sx={{ px: { xs: 2, md: 4 } }}>
        <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 3 }}>
          Productos destacados
        </Typography>
        <Grid container spacing={4}>
          {productos.map((prod) => (
            <Grid item xs={12} sm={6} md={4} key={prod.id}>
              <Paper elevation={3} sx={{ p: 2, borderRadius: 3, position: 'relative' }}>
                <img
                  src={prod.imagen}
                  alt={prod.nombre}
                  style={{ width: '100%', borderRadius: 8, objectFit: 'cover' }}
                />
                <Typography variant="h6" sx={{ mt: 2, fontWeight: 'bold' }}>
                  {prod.nombre}
                </Typography>
                <Typography variant="body2" sx={{ color: 'text.secondary', mb: 1 }}>
                  Precio: {prod.precio} USD
                </Typography>
                {prod.descuento && (
                  <Chip
                    label={`-${prod.descuento}%`}
                    color="error"
                    size="small"
                    sx={{ position: 'absolute', top: 10, right: 10 }}
                  />
                )}
                {isLoggedIn && (
                  <Button
                    variant="contained"
                    color="success"
                    fullWidth
                    onClick={() => handleCompra(prod.id)}
                    sx={{
                      borderRadius: 30,
                      py: { xs: 1, md: 1.5 },
                      fontWeight: 'bold',
                      fontSize: { xs: '0.9rem', md: '1rem' },
                      mt: 2
                    }}
                  >
                    Comprar
                  </Button>
                )}
              </Paper>
            </Grid>
          ))}
        </Grid>

        {/* Bloque de sesiÃ³n si no estÃ¡ logueado */}
        {!isLoggedIn && (
          <Box sx={{ mt: 5, textAlign: 'center' }}>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Debes iniciar sesiÃ³n para realizar compras ð
            </Typography>
            <Button
              variant="outlined"
              href="/login"
              sx={{
                color: '#2e7d32',
                borderColor: '#2e7d32',
                fontWeight: 'bold',
                '&:hover': {
                  backgroundColor: '#e8f5e9',
                  borderColor: '#1b5e20'
                }
              }}
            >
              Iniciar sesiÃ³n
            </Button>
          </Box>
        )}
      </Box>
    </Box>
  );
}
