import React from 'react';
import {
  Modal,
  Box,
  IconButton,
  Button
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import LoginForm from '../forms/LoginForm';

export default function LoginModal({ open, onClose }) {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [emailActual, setEmailActual] = React.useState(localStorage.getItem('token-email'));

  React.useEffect(() => {
    const checkToken = () => {
      const token = localStorage.getItem('token');
      const email = localStorage.getItem('token-email');
      setIsLoggedIn(!!token);
      setEmailActual(email);
    };

    window.addEventListener('storage', checkToken);
    checkToken();

    return () => window.removeEventListener('storage', checkToken);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('token-email');
    setIsLoggedIn(false);
    setEmailActual(null);
    if (onClose) onClose();
  };

  return (
    <Modal
      open={open}
      onClose={onClose}
      aria-labelledby="modal-login"
      aria-describedby="login-accessible"
      BackdropProps={{
        sx: {
          backgroundColor: '#fff' // Fondo plano, sin blur ni opacidad
        }
      }}
    >
      <Box
        role="dialog"
        aria-modal="true"
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          bgcolor: 'white',
          p: 4,
          borderRadius: 3,
          width: { xs: '90%', sm: 420 },
          boxShadow: '0 10px 25px rgba(46,125,50,0.3)',
          outline: 0,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
        }}
      >
        {/* Botón cerrar modal */}
        <IconButton
          onClick={onClose}
          sx={{
            position: 'absolute',
            top: 12,
            right: 12,
            color: '#388e3c'
          }}
          aria-label="Cerrar login"
        >
          <CloseIcon />
        </IconButton>

        {/* Formulario institucional limpio */}
        {!isLoggedIn && (
          <LoginForm
            onClose={onClose}
            onLoginSuccess={() => {
              setIsLoggedIn(true);
              setEmailActual(localStorage.getItem('token-email'));
            }}
          />
        )}

        {/* Sesión activa */}
        {isLoggedIn && emailActual && (
          <Button
            variant="outlined"
            color="error"
            fullWidth
            onClick={handleLogout}
          >
            Cerrar sesión
          </Button>
        )}
      </Box>
    </Modal>
  );
}
