import React from 'react';
import { Box, Typography, Grid, Card, CardMedia, CardContent } from '@mui/material';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function PantallaInicio() {
  const imagenesCarrusel = [
    '/imagenes/carrusel1.jpg',
    '/imagenes/carrusel2.jpg',
    '/imagenes/carrusel3.jpg',
    '/imagenes/carrusel4.jpg'
  ];

  const categorias = [
    { nombre: 'Vegetales', imagen: '/images/vegetales.jpg' },
    { nombre: 'Ganado', imagen: '/images/ganado.jpg' },
    { nombre: 'Semillas', imagen: '/images/semillas.jpg' }
  ];

  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false
  };

  return (
    <Box>
      {/* üñºÔ∏è Carrusel autom√°tico */}
      <Slider {...settings}>
        {imagenesCarrusel.map((img, index) => (
          <Box key={index}>
            <img
              src={img}
              alt={`slide-${index}`}
              style={{ width: '100%', height: '70vh', objectFit: 'cover' }}
            />
          </Box>
        ))}
      </Slider>

      {/* üå± T√≠tulo principal */}
      <Typography variant="h4" align="center" sx={{ mt: 4, fontWeight: 'bold' }}>
        Explora nuestros productos agr√≠colas
      </Typography>

      {/* üß∫ Tarjetas tem√°ticas */}
      <Grid container spacing={3} sx={{ p: 4 }}>
        {categorias.map((item, index) => (
          <Grid item xs={12} sm={4} key={index}>
            <Card
              sx={{
                cursor: 'pointer',
                transition: 'transform 0.3s',
                '&:hover': { transform: 'scale(1.05)' }
              }}
            >
              <CardMedia
                component="img"
                height="200"
                image={item.imagen}
                alt={item.nombre}
              />
              <CardContent>
                <Typography variant="h6" align="center">{item.nombre}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
