import React, { createContext, useState } from 'react';

export const AccessibilityContext = createContext();

export const AccessibilityProvider = ({ children }) => {
  const [altoContraste, setAltoContraste] = useState(false);
  const [modoDislexia, setModoDislexia] = useState(false);
  const [tamaÃ±oTexto, setTamaÃ±oTexto] = useState(1); // ðŸ”¤ 1 = 100% del texto original

  // Cambios en incrementos proporcionales
  const aumentarTexto = () => setTamaÃ±oTexto((prev) => Math.min(prev + 0.1, 1.6)); // hasta 160%
  const disminuirTexto = () => setTamaÃ±oTexto((prev) => Math.max(prev - 0.1, 0.7)); // hasta 70%
  const toggleContraste = () => setAltoContraste((prev) => !prev);
  const toggleDislexia = () => setModoDislexia((prev) => !prev);

  return (
    <AccessibilityContext.Provider value={{
      altoContraste,
      modoDislexia,
      tamaÃ±oTexto,
      toggleContraste,
      toggleDislexia,
      aumentarTexto,
      disminuirTexto
    }}>
      <div style={{
        backgroundColor: altoContraste
          ? '#000'
          : modoDislexia
            ? '#f4f1e8'
            : '#fff',
        color: altoContraste ? '#fff' : '#000',
        fontSize: `${tamaÃ±oTexto}rem`, // âœ… Solo afecta el tamaÃ±o del texto
        fontFamily: modoDislexia ? '"Lexend Deca", Arial, sans-serif' : 'Arial, sans-serif',
        lineHeight: modoDislexia ? '1.8' : '1.5',
        letterSpacing: modoDislexia ? '0.06em' : 'normal',
        padding: modoDislexia ? '2rem 1rem' : '0',
        margin: modoDislexia ? '0 auto' : '0',
        maxWidth: modoDislexia ? '1000px' : 'none',
        transition: 'font-size 0.3s ease, background-color 0.3s ease'
      }}>
        {children}
      </div>
    </AccessibilityContext.Provider>
  );
};
