import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Snackbar,
  Alert
} from '@mui/material';

export default function ContactoForm({ onClose }) {
  const [formData, setFormData] = React.useState({
    nombre: '',
    correo: '',
    asunto: '',
    mensaje: ''
  });

  const [errores, setErrores] = React.useState({});
  const [enviado, setEnviado] = React.useState(false);

  const validar = () => {
    const newErrors = {};
    const correoRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!formData.nombre.trim()) newErrors.nombre = 'Este campo es obligatorio';
    if (!formData.correo || !correoRegex.test(formData.correo)) newErrors.correo = 'Ingrese un correo válido';
    if (!formData.asunto.trim()) newErrors.asunto = 'Debe ingresar un asunto';
    if (!formData.mensaje.trim()) newErrors.mensaje = 'Debe escribir un mensaje';

    setErrores(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validar()) {
      console.log('Mensaje enviado:', formData); // Simulación
      setEnviado(true);
      if (onClose) onClose();
    }
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      noValidate
      sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}
      aria-labelledby="contacto-instrucciones"
    >
      {/* Introducción */}
      <Typography id="contacto-instrucciones" variant="body1" sx={{ mb: 1 }}>
        ¿Tienes preguntas o sugerencias? Completa el siguiente formulario y te responderemos en un plazo máximo de 48 horas.
      </Typography>

      {/* Campos */}
      <TextField
        name="nombre"
        label="Nombre"
        autoComplete="name"
        fullWidth
        value={formData.nombre}
        onChange={(e) => setFormData({ ...formData, nombre: e.target.value })}
        error={!!errores.nombre}
        helperText={errores.nombre}
      />

      <TextField
        name="correo"
        label="Correo electrónico"
        autoComplete="email"
        fullWidth
        value={formData.correo}
        onChange={(e) => setFormData({ ...formData, correo: e.target.value })}
        error={!!errores.correo}
        helperText={errores.correo}
      />

      <TextField
        name="asunto"
        label="Asunto"
        fullWidth
        value={formData.asunto}
        onChange={(e) => setFormData({ ...formData, asunto: e.target.value })}
        error={!!errores.asunto}
        helperText={errores.asunto}
      />

      <TextField
        name="mensaje"
        label="Mensaje"
        fullWidth
        multiline
        rows={5}
        value={formData.mensaje}
        onChange={(e) => setFormData({ ...formData, mensaje: e.target.value })}
        error={!!errores.mensaje}
        helperText={errores.mensaje}
      />

      {/* Acciones */}
      <Box sx={{ display: 'flex', gap: 2 }}>
        <Button
          type="submit"
          variant="contained"
          fullWidth
          sx={{
            bgcolor: '#388e3c',
            '&:hover': { bgcolor: '#2e7d32' },
            color: 'white',
            fontWeight: 'bold'
          }}
        >
          Enviar
        </Button>
        <Button onClick={onClose} variant="outlined" color="secondary" fullWidth>
          Cancelar
        </Button>
      </Box>

      {/* Confirmación visual */}
      <Snackbar
        open={enviado}
        autoHideDuration={4000}
        onClose={() => setEnviado(false)}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert
          severity="success"
          variant="filled"
          onClose={() => setEnviado(false)}
          sx={{ bgcolor: '#1976d2', color: 'white' }}
        >
          ¡Tu mensaje ha sido enviado correctamente!
        </Alert>
      </Snackbar>
    </Box>
  );
}
