import React from 'react';
import { Box, Typography, IconButton, TextField, Button, Link } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

export default function LoginModal({ onClose }) {
  return (
    <Box
      sx={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        bgcolor: 'rgba(0,0,0,0.5)',
        zIndex: 1200,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
      }}
    >
      <Box
        sx={{
          position: 'relative',
          width: { xs: '90%', sm: 420 },
          bgcolor: 'white',
          borderRadius: 3,
          boxShadow: 6,
          px: 4,
          pt: 4,
          pb: 3
        }}
      >
        {/* Botón cerrar */}
        <IconButton onClick={onClose} sx={{ position: 'absolute', top: 12, right: 12 }}>
          <CloseIcon />
        </IconButton>

        {/* Título */}
        <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#2e7d32', mb: 3 }}>
          Iniciar sesión
        </Typography>

        {/* Campo correo */}
        <TextField
          label="Correo electrónico"
          fullWidth
          placeholder="Ej. nombre@mail.com"
          sx={{ mb: 2 }}
        />

        {/* Campo contraseña */}
        <TextField
          label="Contraseña"
          type="password"
          fullWidth
          placeholder="Escribe tu contraseña"
          sx={{ mb: 1 }}
        />

        {/* Enlace recuperar */}
        <Typography variant="body2" sx={{ textAlign: 'right', mb: 3 }}>
          <Link href="#" underline="hover" sx={{ fontSize: '0.875rem' }}>
            Olvidé mi contraseña
          </Link>
        </Typography>

        {/* Botón entrar */}
        <Button
          fullWidth
          variant="contained"
          sx={{
            bgcolor: '#fb8c00',
            color: 'white',
            fontWeight: 'bold',
            textTransform: 'none',
            py: 1.2,
            fontSize: '0.95rem',
            borderRadius: 2,
            mb: 2,
            '&:hover': { bgcolor: '#f57c00' }
          }}
        >
          Entrar
        </Button>

        {/* Enlace registro */}
        <Typography variant="body2" sx={{ textAlign: 'center' }}>
          ¿No tiene una cuenta?{' '}
          <Link href="#" underline="hover" sx={{ fontWeight: 'bold' }}>
            Regístrate
          </Link>
        </Typography>
      </Box>
    </Box>
  );
}
