import React, { createContext, useState, useMemo } from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';

export const AccessibilityContext = createContext();

export const AccessibilityProvider = ({ children }) => {
  const [altoContraste, setAltoContraste] = useState(false);
  const [modoDislexia, setModoDislexia] = useState(false);
  const [tama침oTexto, setTama침oTexto] = useState(1); // 游댟 escala 1 = 100%

  const aumentarTexto = () => setTama침oTexto((prev) => Math.min(prev + 0.1, 1.6));
  const disminuirTexto = () => setTama침oTexto((prev) => Math.max(prev - 0.1, 0.7));
  const toggleContraste = () => setAltoContraste((prev) => !prev);
  const toggleDislexia = () => setModoDislexia((prev) => !prev);

  // 游꿛 Genera tema din치mico para MUI
  const theme = useMemo(() =>
    createTheme({
      typography: {
        htmlFontSize: 16,
        fontSize: 16 * tama침oTexto, // escala proporcional para todas las variantes
      },
    }), [tama침oTexto]);

  return (
    <AccessibilityContext.Provider value={{
      altoContraste,
      modoDislexia,
      tama침oTexto,
      toggleContraste,
      toggleDislexia,
      aumentarTexto,
      disminuirTexto
    }}>
      <ThemeProvider theme={theme}>
        <div style={{
          backgroundColor: altoContraste
            ? '#000'
            : modoDislexia
              ? '#f4f1e8'
              : '#fff',
          color: altoContraste ? '#fff' : '#000',
          fontFamily: modoDislexia ? '"Lexend Deca", Arial, sans-serif' : 'Arial, sans-serif',
          lineHeight: modoDislexia ? '1.8' : '1.5',
          letterSpacing: modoDislexia ? '0.06em' : 'normal',
          padding: modoDislexia ? '2rem 1rem' : '0',
          margin: modoDislexia ? '0 auto' : '0',
          maxWidth: modoDislexia ? '1000px' : 'none',
          transition: 'background-color 0.3s ease'
        }}>
          {children}
        </div>
      </ThemeProvider>
    </AccessibilityContext.Provider>
  );
};
