import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Grid,
  Button,
  Snackbar,
  Alert,
  InputAdornment,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import EmailIcon from '@mui/icons-material/Email';
import SubjectIcon from '@mui/icons-material/Subject';
import SendIcon from '@mui/icons-material/Send';
import CancelIcon from '@mui/icons-material/Cancel';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';
import { useNavigate } from 'react-router-dom';

export default function ContactoForm() {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [formData, setFormData] = React.useState({
    nombre: '',
    correo: '',
    asunto: '',
    mensaje: '',
  });

  const [errores, setErrores] = React.useState({});
  const [enviado, setEnviado] = React.useState(false);

  const validar = () => {
    const newErrors = {};
    const correoRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!formData.nombre.trim()) newErrors.nombre = 'Este campo es obligatorio';
    if (!formData.correo || !correoRegex.test(formData.correo))
      newErrors.correo = 'Ingrese un correo válido';
    if (!formData.asunto.trim()) newErrors.asunto = 'Debe ingresar un asunto';
    if (!formData.mensaje.trim()) newErrors.mensaje = 'Debe escribir un mensaje';

    setErrores(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validar()) {
      // Aquí iría el envío real, por ahora simulamos
      console.log('Mensaje enviado:', formData);
      setEnviado(true);
      setFormData({ nombre: '', correo: '', asunto: '', mensaje: '' });

      setTimeout(() => navigate(-1), 3000);
    }
  };

  const handleCancel = () => {
    navigate(-1);
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        bgcolor: '#e8f5e9',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        px: isMobile ? 2 : 4,
        py: 6,
      }}
    >
      {/* Banner superior con logo e ícono */}
      <Box
        sx={{
          mb: 4,
          display: 'flex',
          alignItems: 'center',
          gap: 2,
          justifyContent: 'center',
          color: '#2e7d32',
          fontWeight: 'bold',
          fontSize: isMobile ? '1.5rem' : '2rem',
          userSelect: 'none',
        }}
      >
        <SupportAgentIcon fontSize="large" />
        <Typography component="h1">Solicita tu asesoría personalizada</Typography>
      </Box>

      {/* Formulario */}
      <Box
        component="form"
        onSubmit={handleSubmit}
        noValidate
        sx={{
          maxWidth: 700,
          bgcolor: 'white',
          mx: 'auto',
          p: 4,
          borderRadius: 3,
          boxShadow: '0 8px 24px rgba(0,0,0,0.12)',
        }}
      >
        <Typography
          variant="subtitle1"
          sx={{ mb: 3, color: '#4caf50', fontWeight: 600, textAlign: 'center' }}
        >
          ¿Necesitás ayuda con tus productos, accesibilidad o compras institucionales? 
          Nuestro equipo te responderá en menos de <strong>48 horas</strong>.
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <TextField
              name="nombre"
              label="Nombre"
              autoComplete="name"
              fullWidth
              value={formData.nombre}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, nombre: e.target.value }))
              }
              error={!!errores.nombre}
              helperText={errores.nombre}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <AccountCircleIcon sx={{ color: '#777' }} />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            <TextField
              name="correo"
              label="Correo electrónico"
              autoComplete="email"
              fullWidth
              value={formData.correo}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, correo: e.target.value }))
              }
              error={!!errores.correo}
              helperText={errores.correo}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <EmailIcon sx={{ color: '#777' }} />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              name="asunto"
              label="Asunto"
              fullWidth
              value={formData.asunto}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, asunto: e.target.value }))
              }
              error={!!errores.asunto}
              helperText={errores.asunto}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SubjectIcon sx={{ color: '#777' }} />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              name="mensaje"
              label="Mensaje"
              multiline
              rows={6}
              fullWidth
              value={formData.mensaje}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, mensaje: e.target.value }))
              }
              error={!!errores.mensaje}
              helperText={errores.mensaje}
            />
          </Grid>
        </Grid>

        <Box
          sx={{
            mt: 4,
            display: 'flex',
            justifyContent: 'space-between',
            gap: 2,
            flexDirection: isMobile ? 'column' : 'row',
          }}
        >
          <Button
            type="submit"
            variant="contained"
            startIcon={<SendIcon />}
            fullWidth={isMobile}
            sx={{
              bgcolor: '#388e3c',
              fontWeight: 'bold',
              fontSize: '1rem',
              py: 1.5,
              borderRadius: 3,
              transition: 'background-color 0.3s',
              '&:hover': { bgcolor: '#2e7d32' },
            }}
          >
            Solicitar asesoría
          </Button>

          <Button
            variant="outlined"
            color="error"
            startIcon={<CancelIcon />}
            onClick={handleCancel}
            fullWidth={isMobile}
            sx={{
              fontWeight: 'bold',
              fontSize: '1rem',
              py: 1.5,
              borderRadius: 3,
            }}
          >
            Cancelar
          </Button>
        </Box>
      </Box>

      {/* Snackbar de confirmación */}
      <Snackbar
        open={enviado}
        autoHideDuration={4000}
        onClose={() => setEnviado(false)}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert
          severity="success"
          variant="filled"
          onClose={() => setEnviado(false)}
          sx={{ bgcolor: '#2e7d32', color: 'white' }}
        >
          ¡Tu solicitud ha sido enviada correctamente!
        </Alert>
      </Snackbar>
    </Box>
  );
}
