import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Grid,
  Button,
  Snackbar,
  Alert,
  useTheme,
  useMediaQuery,
  InputAdornment,
} from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import EmailIcon from '@mui/icons-material/Email';
import SubjectIcon from '@mui/icons-material/Subject';
import SendIcon from '@mui/icons-material/Send';
import CancelIcon from '@mui/icons-material/Cancel';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';
import { useNavigate } from 'react-router-dom';
import MainLayout from '../../components/layout/MainLayout';

function InputWithIcon({
  label,
  name,
  icon: Icon,
  value,
  onChange,
  error,
  helperText,
  multiline = false,
  rows = 1
}) {
  return (
    <TextField
      fullWidth
      multiline={multiline}
      rows={rows}
      variant="outlined"
      name={name}
      label={label}
      value={value}
      onChange={onChange}
      error={error}
      helperText={error ? helperText : ' '}
      InputProps={{
        startAdornment: (
          <InputAdornment position="start">
            <Icon color={error ? 'error' : 'action'} />
          </InputAdornment>
        )
      }}
      InputLabelProps={{
        sx: {
          fontWeight: 500,
          color: error ? '#d32f2f' : '#388e3c',
          '&.Mui-focused': {
            color: error ? '#d32f2f' : '#2e7d32',
          },
          transition: 'all 0.3s ease'
        }
      }}
      sx={{
        bgcolor: '#ffffff',
        '& .MuiOutlinedInput-root': {
          borderRadius: 2,
          '& fieldset': {
            borderColor: error ? '#d32f2f' : '#c8e6c9',
            borderWidth: '2px'
          },
          '&:hover fieldset': {
            borderColor: error ? '#b71c1c' : '#66bb6a'
          },
          '&.Mui-focused fieldset': {
            borderColor: error ? '#d32f2f' : '#43a047',
            boxShadow: error
              ? '0 0 6px rgba(211,47,47,0.4)'
              : '0 0 8px rgba(67,160,71,0.3)',
            transition: 'box-shadow 0.3s ease'
          }
        },
        '& input, & textarea': {
          fontWeight: 500
        }
      }}
    />
  );
}
export default function ContactoForm() {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [formData, setFormData] = React.useState({
    nombre: '',
    correo: '',
    asunto: '',
    mensaje: '',
  });

  const [errores, setErrores] = React.useState({});
  const [enviado, setEnviado] = React.useState(false);

  const validar = () => {
    const newErrors = {};
    const correoRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!formData.nombre.trim()) newErrors.nombre = 'Este campo es obligatorio';
    if (!correoRegex.test(formData.correo)) newErrors.correo = 'Correo inválido';
    if (!formData.asunto.trim()) newErrors.asunto = 'Ingrese un asunto';
    if (!formData.mensaje.trim()) newErrors.mensaje = 'Escriba un mensaje';

    setErrores(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validar()) {
      console.log('Formulario enviado:', formData);
      setEnviado(true);
      setFormData({ nombre: '', correo: '', asunto: '', mensaje: '' });
      setTimeout(() => navigate(-1), 3000);
    }
  };

  const handleCancel = () => navigate(-1);

  return (
    <MainLayout>
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #4caf50, #81c784, #a5d6a7)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          p: isMobile ? 2 : 5,
        }}
      >
        <Box
          component="form"
          onSubmit={handleSubmit}
          noValidate
          sx={{
            width: '100%',
            maxWidth: 720,
            bgcolor: 'white',
            borderRadius: 4,
            boxShadow: '0 12px 40px rgba(0,0,0,0.15)',
            px: isMobile ? 3 : 6,
            py: isMobile ? 4 : 6,
          }}
        >
          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              gap: 1.5,
              color: '#1b5e20',
              fontWeight: 700,
              fontSize: isMobile ? '1.6rem' : '2.4rem',
              mb: 4,
              justifyContent: 'center',
            }}
          >
            <SupportAgentIcon fontSize="inherit" />
            <Typography component="h1">Asesoría personalizada</Typography>
          </Box>

          <Typography
            variant="subtitle1"
            sx={{
              mb: 5,
              color: '#2e7d32',
              fontWeight: 500,
              textAlign: 'center',
              fontSize: isMobile ? '1rem' : '1.2rem',
            }}
          >
            ¿Tienes dudas sobre nuestros productos o servicios? <br />
            Nuestro equipo te responderá en menos de <strong>48 horas</strong>.
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <InputWithIcon
                label="Nombre completo"
                name="nombre"
                icon={AccountCircleIcon}
                value={formData.nombre}
                onChange={(e) => setFormData({ ...formData, nombre: e.target.value })}
                error={!!errores.nombre}
                helperText={errores.nombre}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <InputWithIcon
                label="Correo electrónico"
                name="correo"
                icon={EmailIcon}
                value={formData.correo}
                onChange={(e) => setFormData({ ...formData, correo: e.target.value })}
                error={!!errores.correo}
                helperText={errores.correo}
              />
            </Grid>
            <Grid item xs={12}>
              <InputWithIcon
                label="Asunto"
                name="asunto"
                icon={SubjectIcon}
                value={formData.asunto}
                onChange={(e) => setFormData({ ...formData, asunto: e.target.value })}
                error={!!errores.asunto}
                helperText={errores.asunto}
              />
            </Grid>
            <Grid item xs={12}>
              <InputWithIcon
                label="Mensaje detallado"
                name="mensaje"
                icon={SubjectIcon}
                multiline
                rows={6}
                value={formData.mensaje}
                onChange={(e) => setFormData({ ...formData, mensaje: e.target.value })}
                error={!!errores.mensaje}
                helperText={errores.mensaje}
              />
            </Grid>
          </Grid>

          <Box
            sx={{
              mt: 5,
              display: 'flex',
              flexDirection: isMobile ? 'column' : 'row',
              gap: 2,
              justifyContent: 'center',
            }}
          >
            <Button
              type="submit"
              variant="contained"
              startIcon={<SendIcon />}
              fullWidth={isMobile}
              sx={{
                background: 'linear-gradient(45deg, #388e3c, #66bb6a)',
                fontWeight: 600,
                fontSize: '1rem',
                py: 1.5,
                borderRadius: 3,
                textTransform: 'none',
                '&:hover': {
                  background: 'linear-gradient(45deg, #2e7d32, #4caf50)',
                },
              }}
            >
              Enviar mensaje
            </Button>

            <Button
              variant="outlined"
              color="error"
              startIcon={<CancelIcon />}
              onClick={handleCancel}
              fullWidth={isMobile}
              sx={{
                fontWeight: 600,
                fontSize: '1rem',
                py: 1.5,
                borderRadius: 3,
                textTransform: 'none',
              }}
            >
              Cancelar
            </Button>
          </Box>

          <Snackbar
            open={enviado}
            autoHideDuration={4000}
            onClose={() => setEnviado(false)}
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
          >
            <Alert
              severity="success"
              variant="filled"
              onClose={() => setEnviado(false)}
              sx={{
                bgcolor: '#2e7d32',
                color: 'white',
                fontWeight: '600',
              }}
            >
              ¡Tu solicitud ha sido enviada correctamente!
            </Alert>
          </Snackbar>
        </Box>
      </Box>
    </MainLayout>
  );
}
