import React from 'react';
import { Box, Typography, Button } from '@mui/material';

const secciones = [
  {
    imagen: '/imagenes/carrusel1.jpg',
    titulo: 'Ganadería responsable para alimentar al mundo',
    subtitulo: 'Cuidamos cada paso del proceso productivo',
    boton: 'Explora la categoría Ganado'
  },
  {
    imagen: '/imagenes/carrusel2.jpg',
    titulo: 'La naturaleza nos da vida, nosotros la protegemos',
    subtitulo: 'Cultivamos con respeto por la tierra',
    boton: 'Ver iniciativas ecológicas'
  },
  {
    imagen: '/imagenes/carrusel3.jpg',
    titulo: 'Sembramos futuro con cada semilla',
    subtitulo: 'Innovación desde el inicio del ciclo productivo',
    boton: 'Explora la categoría Semillas'
  },
  {
    imagen: '/imagenes/carrusel4.jpg',
    titulo: 'Frutas frescas, sabor natural directo del campo',
    subtitulo: 'Calidad agrícola para todos',
    boton: 'Explora la categoría Frutas'
  }
];

export default function PantallaInicio() {
  return (
    <Box>
      {secciones.map((item, index) => (
        <Box
          key={index}
          sx={{
            position: 'relative',
            height: '100vh',
            backgroundImage: `url(${item.imagen})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            textAlign: 'center',
            color: '#fff',
            px: 2
          }}
        >
          {/* Overlay oscuro */}
          <Box
            sx={{
              position: 'absolute',
              inset: 0,
              backgroundColor: 'rgba(0, 0, 0, 0.45)',
              zIndex: 1
            }}
          />

          {/* Contenido sobre imagen */}
          <Box sx={{ zIndex: 2 }}>
            <Typography variant="h3" sx={{ fontWeight: 'bold', textShadow: '2px 2px 5px #000' }}>
              {item.titulo}
            </Typography>
            <Typography variant="h6" sx={{ mt: 2, textShadow: '1px 1px 3px #000' }}>
              {item.subtitulo}
            </Typography>
            <Button variant="contained" color="success" sx={{ mt: 4 }}>
              {item.boton}
            </Button>
          </Box>
        </Box>
      ))}
    </Box>
  );
}
