import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Grid,
  Paper
} from '@mui/material';
import productos from '../../data/productos.json';

export default function Comprar() {
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('token'));

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      navigate('/login');
    } else {
      navigate(`/comprar/${id}`);
    }
  };

  return (
    <Box>
      {/* Banner institucional */}
      <Box
        sx={{
          width: '100%',
          height: { xs: 220, md: 400 },
          backgroundImage: 'url("/imagenes/banner-compra.webp")',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          px: { xs: 2, md: 8 },
          textAlign: 'center',
          mb: 4
        }}
      >
        <Typography
          variant="h4"
          sx={{
            fontSize: { xs: '1.6rem', md: '2.8rem' },
            color: '#fff',
            fontWeight: 'bold'
          }}
        >
          ¡Compra fácil, rápido y seguro!
        </Typography>
      </Box>

      {/* Catálogo */}
      <Box sx={{ px: { xs: 2, md: 4 }, pb: { xs: 4, md: 6 } }}>
        <Typography
          variant="h5"
          sx={{ fontWeight: 'bold', mb: { xs: 2, md: 3 }, textAlign: 'center' }}
        >
          Catálogo de productos
        </Typography>

        <Grid container spacing={4}>
          {productos.map((prod) => (
            <Grid item xs={12} sm={6} md={4} key={prod.id}>
              <Paper elevation={3} sx={{ p: 2, borderRadius: 3 }}>
                <img
                  src={prod.imagen}
                  alt={prod.nombre}
                  style={{
                    width: '100%',
                    borderRadius: 8,
                    objectFit: 'cover'
                  }}
                />
                <Typography variant="h6" sx={{ mt: 2, fontWeight: 'bold' }}>
                  {prod.nombre}
                </Typography>
                <Typography variant="body2" sx={{ color: 'text.secondary', mb: 2 }}>
                  {prod.precio} USD
                </Typography>

                <Button
                  variant="contained"
                  color="success"
                  fullWidth
                  onClick={() => handleCompra(prod.id)}
                  sx={{
                    borderRadius: 30,
                    py: { xs: 1, md: 1.5 },
                    fontWeight: 'bold',
                    fontSize: { xs: '0.9rem', md: '1rem' }
                  }}
                >
                  Comprar
                </Button>

                {!isLoggedIn && (
                  <Typography
                    variant="body2"
                    sx={{
                      mt: 1,
                      color: 'error.main',
                      textAlign: 'center',
                      fontSize: { xs: '0.8rem', md: '0.9rem' }
                    }}
                  >
                    Debes iniciar sesión o registrarte para comprar 🔐
                  </Typography>
                )}
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
}
