import React, { useState, useContext } from 'react';
import {
  Box, Fab, Popper, Paper, Button, Typography, Fade
} from '@mui/material';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import { AccessibilityContext } from '../../context/AccessibilityContext';

export default function AccessibilityFAB() {
  const [open, setOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);

  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto
  } = useContext(AccessibilityContext);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
    setOpen((prev) => !prev);
  };

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: 24,
        right: 24,
        zIndex: 1300
      }}
    >
      <Fab
        onClick={handleClick}
        aria-label="Accesibilidad"
        sx={{
          backgroundColor: '#ffffffee', // blanco con transparencia ligera
          color: '#1976d2',
          border: '2px solid #1976d2',
          boxShadow: '0 8px 24px rgba(0,0,0,0.35)',
          width: 64,
          height: 64,
          borderRadius: '16px',
          backdropFilter: 'blur(6px)',
          '&:hover': {
            backgroundColor: '#ffffff',
            transform: 'scale(1.1)',
            boxShadow: '0 12px 32px rgba(0,0,0,0.5)'
          },
          transition: 'all 0.3s ease-in-out'
        }}
      >
        <AccessibilityNewIcon fontSize="large" />
      </Fab>

      <Popper open={open} anchorEl={anchorEl} transition placement="top-end">
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={250}>
            <Paper
              elevation={6}
              sx={{
                p: 2,
                mt: 1,
                minWidth: 240,
                backgroundColor: '#f9f9f9',
                borderRadius: 6,
                boxShadow: '0 6px 20px rgba(0,0,0,0.25)',
                border: '1px solid #e0e0e0'
              }}
            >
              <Typography
                variant="h6"
                gutterBottom
                sx={{ fontWeight: 'bold', color: '#1976d2', mb: 2 }}
              >
                ⚙️ Accesibilidad
              </Typography>

              <Button
                variant="contained"
                color={altoContraste ? 'secondary' : 'primary'}
                fullWidth
                onClick={toggleContraste}
                sx={{ mb: 1, textTransform: 'none' }}
              >
                {altoContraste ? 'Desactivar Contraste' : 'Activar Contraste'}
              </Button>

              <Button
                variant="contained"
                color={modoDislexia ? 'secondary' : 'primary'}
                fullWidth
                onClick={toggleDislexia}
                sx={{ mb: 1, textTransform: 'none' }}
              >
                {modoDislexia ? 'Desactivar Dislexia' : 'Activar Dislexia'}
              </Button>

              <Typography
                variant="caption"
                color="text.secondary"
                sx={{ mt: 1 }}
              >
                Escala de texto: <strong>{Math.round(tamañoTexto * 100)}%</strong>
              </Typography>

              <Box sx={{ mt: 1.5, display: 'flex', justifyContent: 'space-between' }}>
                <Button
                  onClick={disminuirTexto}
                  variant="outlined"
                  size="small"
                  sx={{ minWidth: 36 }}
                >
                  –
                </Button>
                <Button
                  onClick={aumentarTexto}
                  variant="outlined"
                  size="small"
                  sx={{ minWidth: 36 }}
                >
                  +
                </Button>
              </Box>
            </Paper>
          </Fade>
        )}
      </Popper>
    </Box>
  );
}
