import React, { useState, useEffect } from 'react';
import {
  Box,
  TextField,
  MenuItem,
  Autocomplete,
  Switch,
  IconButton,
  Typography,
  CircularProgress,
  FormControlLabel
} from '@mui/material';
import ClearIcon from '@mui/icons-material/Clear';
import productos from '../../data/productos.json';
import { useNavigate } from 'react-router-dom';

export default function BuscadorNavbarAvanzado() {
  const navigate = useNavigate();

  const nombres = productos.map(p => p.nombre);
  const [nombre, setNombre] = useState('');
  const [categoria, setCategoria] = useState('');
  const [disponible, setDisponible] = useState(false);
  const [resultados, setResultados] = useState([]);
  const [cargando, setCargando] = useState(false);

  useEffect(() => {
    setCargando(true);
    const timer = setTimeout(() => {
      const filtrados = productos.filter(p => {
        const matchNombre = nombre ? p.nombre.toLowerCase().includes(nombre.toLowerCase()) : true;
        const matchCat = categoria ? p.categoria === categoria : true;
        const matchDisp = disponible ? p.disponible : true;
        return matchNombre && matchCat && matchDisp;
      });
      setResultados(filtrados);
      setCargando(false);
    }, 400);
    return () => clearTimeout(timer);
  }, [nombre, categoria, disponible]);

  const limpiar = () => {
    setNombre('');
    setCategoria('');
    setDisponible(false);
    setResultados([]);
  };

  const irADetalle = (e, valor) => {
    const producto = productos.find(p => p.nombre === valor);
    if (producto) navigate(`/productos/detalle/${producto.id}`);
  };

  return (
    <Box sx={{
      display: 'flex',
      alignItems: 'center',
      gap: 1,
      flexWrap: 'wrap',
      bgcolor: '#ffffff33',
      p: 1,
      borderRadius: 3,
      minWidth: 300
    }}>
      <Autocomplete
        freeSolo
        disableClearable
        options={nombres}
        value={nombre}
        onInputChange={(e, val) => setNombre(val)}
        onChange={irADetalle}
        sx={{ flexGrow: 1, minWidth: 180 }}
        renderInput={(params) => (
          <TextField
            {...params}
            label="Buscar producto"
            InputProps={{
              ...params.InputProps,
              type: 'search'
            }}
            size="small"
          />
        )}
      />

      <TextField
        select
        value={categoria}
        onChange={(e) => setCategoria(e.target.value)}
        label="Categoría"
        size="small"
        sx={{ minWidth: 150 }}
      >
        <MenuItem value="">Todas</MenuItem>
        <MenuItem value="Alimento Perro">Alimento Perro</MenuItem>
        <MenuItem value="Alimento Gato">Alimento Gato</MenuItem>
        <MenuItem value="Limpieza Y Aseo">Limpieza Y Aseo</MenuItem>
        <MenuItem value="Jardinería">Jardinería</MenuItem>
      </TextField>

      <FormControlLabel
        control={
          <Switch
            checked={disponible}
            onChange={(e) => setDisponible(e.target.checked)}
            color="success"
          />
        }
        label="Disponibles"
      />

      <IconButton onClick={limpiar} title="Limpiar filtros">
        <ClearIcon />
      </IconButton>

      {cargando ? (
        <CircularProgress size={24} color="success" />
      ) : (
        <Typography sx={{ fontSize: '0.8rem', fontWeight: 'bold' }}>
          {resultados.length > 0
            ? `Resultados: ${resultados.length}`
            : nombre || categoria || disponible
              ? 'Sin coincidencias'
              : ''}
        </Typography>
      )}
    </Box>
  );
}
