import React, { useState } from 'react';
import {
  Box,
  Autocomplete,
  TextField,
  IconButton
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import productos from '../../data/productos.json';
import { useNavigate } from 'react-router-dom';

export default function BuscadorNavbarAvanzado() {
  const navigate = useNavigate();
  const nombres = productos.map(p => p.nombre);
  const [valorSeleccionado, setValorSeleccionado] = useState('');

  const irADetalle = () => {
    const producto = productos.find(p =>
      p.nombre.toLowerCase().includes(valorSeleccionado.toLowerCase()) ||
      p.categoria.toLowerCase().includes(valorSeleccionado.toLowerCase())
    );
    if (producto) {
      navigate(`/productos/detalle/${producto.id}`);
    } else {
      alert('Producto no encontrado');
    }
  };

  return (
    <Box sx={{
      display: 'flex',
      alignItems: 'center',
      backgroundColor: '#ffffff33',
      padding: '4px 12px',
      borderRadius: '20px',
      '&:hover': { backgroundColor: '#ffffff55' },
      minWidth: 280
    }}>
      <Autocomplete
        freeSolo
        options={nombres}
        value={valorSeleccionado}
        onInputChange={(e, value) => setValorSeleccionado(value)}
        onKeyDown={(e) => e.key === 'Enter' && irADetalle()}
        sx={{ flexGrow: 1 }}
        renderInput={(params) => (
          <TextField
            {...params}
            placeholder="Buscar productoâ€¦"
            variant="standard"
            InputProps={{
              ...params.InputProps,
              disableUnderline: true,
              style: { color: 'white' }
            }}
          />
        )}
      />
      <IconButton
        size="small"
        sx={{ color: 'inherit' }}
        onClick={irADetalle}
      >
        <SearchIcon />
      </IconButton>
    </Box>
  );
}
