import React from 'react';
import { Container, Box, Typography, TextField, Button } from '@mui/material';
import { useForm } from 'react-hook-form';

export default function ContactoForm() {
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm();

  const onSubmit = (data) => {
    console.log('Mensaje enviado:', data);
    // Aquí puedes hacer una petición a tu backend con Axios
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 5 }}>
        <Typography variant="h4" gutterBottom>
          Formulario de Contacto
        </Typography>
        <form onSubmit={handleSubmit(onSubmit)} noValidate aria-label="Formulario de contacto">

          <TextField
            label="Nombre"
            fullWidth
            margin="normal"
            {...register('nombre', { required: 'El nombre es obligatorio' })}
            error={!!errors.nombre}
            helperText={errors.nombre?.message}
            inputProps={{ 'aria-label': 'Campo nombre' }}
          />

          <TextField
            label="Correo electrónico"
            fullWidth
            margin="normal"
            {...register('correo', {
              required: 'El correo es obligatorio',
              pattern: {
                value: /^\S+@\S+\.\S+$/,
                message: 'Correo inválido'
              }
            })}
            error={!!errors.correo}
            helperText={errors.correo?.message}
            inputProps={{ 'aria-label': 'Campo correo electrónico' }}
          />

          <TextField
            label="Asunto"
            fullWidth
            margin="normal"
            {...register('asunto')}
            inputProps={{ 'aria-label': 'Campo asunto' }}
          />

          <TextField
            label="Mensaje"
            fullWidth
            margin="normal"
            multiline
            rows={4}
            {...register('mensaje', { required: 'El mensaje no puede estar vacío' })}
            error={!!errors.mensaje}
            helperText={errors.mensaje?.message}
            inputProps={{ 'aria-label': 'Campo mensaje' }}
          />

          <Button variant="contained" color="primary" type="submit" fullWidth sx={{ mt: 2 }}>
            Enviar mensaje
          </Button>
        </form>
      </Box>
    </Container>
  );
}
