import React from 'react';
import {
  Box, Grid, Typography, Link,
  TextField, Button, Divider
} from '@mui/material';

export default function Footer() {
  return (
    <Box sx={{
      mt: 10,
      px: { xs: 4, sm: 8 },
      py: { xs: 6, sm: 8 },
      backgroundColor: '#111',
      color: '#fff'
    }}>
      <Grid container spacing={4}>
        {/* Contacto institucional */}
        <Grid item xs={12} sm={4}>
          <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 2 }}>
            Agripac
          </Typography>
          <Typography variant="body2" sx={{ lineHeight: 1.6 }}>
            Matriz: General Córdova No. 623 y Padre Solano<br />
            Guayaquil – Ecuador<br />
            Teléfono: (593 - 4) 3703870 – 2560400<br />
            Email: info@agripac.com.ec
          </Typography>
        </Grid>

        {/* Navegación interna */}
        <Grid item xs={6} sm={2}>
          <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
            Nosotros
          </Typography>
          {["Quienes somos", "Nuestra historia", "Responsabilidad", "La escuelita", "Contáctanos"].map((item) => (
            <Typography key={item} variant="body2" sx={{ mb: 0.5 }}>
              <Link href="#" color="inherit" underline="hover">
                {item}
              </Link>
            </Typography>
          ))}
        </Grid>

        <Grid item xs={6} sm={2}>
          <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
            Divisiones
          </Typography>
          {["Banano", "Acuacultura", "Mascotas", "Fertilizantes", "Larvicultura"].map((item) => (
            <Typography key={item} variant="body2" sx={{ mb: 0.5 }}>
              <Link href="#" color="inherit" underline="hover">
                {item}
              </Link>
            </Typography>
          ))}
        </Grid>

        {/* Suscripción y redes */}
        <Grid item xs={12} sm={4}>
          <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
            Newsletter
          </Typography>
          <Typography variant="body2" sx={{ mb: 2 }}>
            Recibe noticias y actualizaciones directamente en tu correo 🌱
          </Typography>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <TextField
              placeholder="Tu correo"
              variant="filled"
              size="small"
              InputProps={{ disableUnderline: true }}
              sx={{
                backgroundColor: '#fff',
                borderRadius: 1,
                flex: 1
              }}
            />
            <Button
              variant="contained"
              sx={{
                bgcolor: '#ffd900',
                color: '#000',
                fontWeight: 'bold',
                px: 2,
                '&:hover': { bgcolor: '#ffeb3b' }
              }}
            >
              Suscribirme
            </Button>
          </Box>
        </Grid>
      </Grid>

      <Divider sx={{ my: 4, borderColor: '#444' }} />

      {/* Copyright */}
      <Typography variant="caption" align="center" sx={{ display: 'block', color: '#aaa' }}>
        © 2024 Agripac S.A. Todos los derechos reservados.
      </Typography>
    </Box>
  );
}
