import React from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Snackbar,
  Alert,
  useTheme,
  useMediaQuery,
  Slide,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import CloseIcon from '@mui/icons-material/Close';
import { useNavigate } from 'react-router-dom';

export default function AsesoriaForm() {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [formData, setFormData] = React.useState({
    nombre: '',
    correo: '',
    asunto: '',
    mensaje: '',
  });

  const [errores, setErrores] = React.useState({});
  const [enviado, setEnviado] = React.useState(false);

  const validar = () => {
    const newErrors = {};
    const correoRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!formData.nombre.trim()) newErrors.nombre = 'Este campo es obligatorio';
    if (!formData.correo || !correoRegex.test(formData.correo))
      newErrors.correo = 'Ingrese un correo válido';
    if (!formData.asunto.trim()) newErrors.asunto = 'Debe ingresar un asunto';
    if (!formData.mensaje.trim()) newErrors.mensaje = 'Debe escribir un mensaje';

    setErrores(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validar()) {
      // Aquí enviarías los datos
      setEnviado(true);
      setFormData({ nombre: '', correo: '', asunto: '', mensaje: '' });
      setTimeout(() => navigate(-1), 3500);
    }
  };

  const handleCancel = () => {
    navigate(-1);
  };

  return (
    <Box
      component="main"
      sx={{
        minHeight: '100vh',
        bgcolor: 'linear-gradient(135deg, #43a047 0%, #1b5e20 100%)',
        background: 'linear-gradient(135deg, #43a047 0%, #1b5e20 100%)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        px: 3,
        py: 5,
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <Box
        component="form"
        onSubmit={handleSubmit}
        noValidate
        sx={{
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          borderRadius: 4,
          boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
          maxWidth: 600,
          width: '100%',
          px: 5,
          py: 6,
          display: 'flex',
          flexDirection: 'column',
          gap: 4,
          position: 'relative',
          overflow: 'hidden',
          '& .MuiInputLabel-root': {
            fontWeight: 600,
            color: '#2e7d32',
          },
          '& .MuiInputBase-root': {
            fontSize: '1rem',
          },
          '& .MuiInput-underline:before': {
            borderBottomColor: '#a5d6a7',
            transition: 'border-color 0.3s',
          },
          '& .MuiInput-underline:hover:not(.Mui-disabled):before': {
            borderBottomColor: '#388e3c',
          },
          '& .MuiInput-underline:after': {
            borderBottomColor: '#2e7d32',
          },
          '& .MuiFormHelperText-root': {
            color: '#d32f2f',
            fontWeight: '600',
            marginTop: 2,
          },
        }}
      >
        <Typography
          variant={isMobile ? 'h4' : 'h3'}
          component="h1"
          sx={{
            color: '#2e7d32',
            fontWeight: '900',
            letterSpacing: 1,
            textAlign: 'center',
            userSelect: 'none',
          }}
        >
          Solicita tu Asesoría Exclusiva
        </Typography>

        <Typography
          variant="subtitle1"
          sx={{
            color: '#4caf50',
            textAlign: 'center',
            fontWeight: 600,
            mb: 2,
            userSelect: 'none',
          }}
        >
          Nuestro equipo experto te responderá en menos de <strong>24 horas</strong>.
        </Typography>

        <TextField
          label="Nombre completo"
          variant="standard"
          fullWidth
          value={formData.nombre}
          onChange={(e) =>
            setFormData((prev) => ({ ...prev, nombre: e.target.value }))
          }
          error={!!errores.nombre}
          helperText={errores.nombre}
          InputLabelProps={{ shrink: true }}
          required
        />

        <TextField
          label="Correo electrónico"
          variant="standard"
          fullWidth
          value={formData.correo}
          onChange={(e) =>
            setFormData((prev) => ({ ...prev, correo: e.target.value }))
          }
          error={!!errores.correo}
          helperText={errores.correo}
          InputLabelProps={{ shrink: true }}
          type="email"
          required
        />

        <TextField
          label="Asunto"
          variant="standard"
          fullWidth
          value={formData.asunto}
          onChange={(e) =>
            setFormData((prev) => ({ ...prev, asunto: e.target.value }))
          }
          error={!!errores.asunto}
          helperText={errores.asunto}
          InputLabelProps={{ shrink: true }}
          required
        />

        <TextField
          label="Mensaje detallado"
          variant="standard"
          fullWidth
          multiline
          rows={5}
          value={formData.mensaje}
          onChange={(e) =>
            setFormData((prev) => ({ ...prev, mensaje: e.target.value }))
          }
          error={!!errores.mensaje}
          helperText={errores.mensaje}
          InputLabelProps={{ shrink: true }}
          required
        />

        <Box
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            gap: isMobile ? 2 : 4,
            flexDirection: isMobile ? 'column' : 'row',
            mt: 3,
          }}
        >
          <Button
            type="submit"
            variant="contained"
            fullWidth={isMobile}
            sx={{
              bgcolor: '#2e7d32',
              fontWeight: 'bold',
              fontSize: '1.1rem',
              py: 1.8,
              borderRadius: 3,
              boxShadow: '0 5px 15px rgba(46,125,50,0.4)',
              transition: 'background-color 0.3s ease, box-shadow 0.3s ease',
              '&:hover': {
                bgcolor: '#1b3d17',
                boxShadow: '0 8px 25px rgba(27,61,23,0.6)',
              },
            }}
            endIcon={<SendIcon />}
          >
            Enviar solicitud
          </Button>

          <Button
            variant="outlined"
            color="error"
            fullWidth={isMobile}
            sx={{
              fontWeight: 'bold',
              fontSize: '1.1rem',
              py: 1.8,
              borderRadius: 3,
              borderWidth: 2,
            }}
            onClick={handleCancel}
            startIcon={<CloseIcon />}
          >
            Cancelar
          </Button>
        </Box>
      </Box>

      <Snackbar
        open={enviado}
        autoHideDuration={4000}
        onClose={() => setEnviado(false)}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
        TransitionComponent={Slide}
      >
        <Alert
          severity="success"
          variant="filled"
          onClose={() => setEnviado(false)}
          sx={{ bgcolor: '#1b5e20', color: 'white', fontWeight: 'bold' }}
        >
          ¡Tu solicitud ha sido enviada exitosamente!
        </Alert>
      </Snackbar>
    </Box>
  );
}
