import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  Chip,
  TextField,
  InputAdornment,
  Avatar,
  Link,
  Stack,
  IconButton
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import productos from '../../data/productos.json';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';

export default function Comprar() {
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('token'));

  // Estado para filtro de categoría
  const categorias = ['Alimento Perro', 'Alimento Gato', 'Limpieza Y Aseo', 'Jardinería'];
  const [filtro, setFiltro] = useState('Alimento Perro');

  const handleCompra = (id) => {
    if (!isLoggedIn) {
      alert('Debes iniciar sesión o registrarte para comprar productos.');
      navigate('/login');
    } else {
      navigate(`/comprar/${id}`);
    }
  };

  // Filtrar productos por categoría
  const destacados = productos.filter(p => p.categoria === filtro).slice(0, 4);
  const masVendidos = productos.filter(p => p.ventas >= 50).slice(0, 4);
  const ultimos = productos.slice(-4);

  const renderCarrusel = (lista) => (
    <Grid container spacing={4}>
      {lista.map(prod => (
        <Grid item xs={12} sm={6} md={3} key={prod.id}>
          <Paper elevation={0} sx={{ p: 2, borderRadius: 2, position: 'relative' }}>
            {/* Badge de descuento */}
            {prod.descuento && (
              <Box
                sx={{
                  position: 'absolute',
                  top: 12,
                  right: 12,
                  bgcolor: '#f44336',
                  color: '#fff',
                  px: 1.5,
                  py: 0.5,
                  borderRadius: 1,
                  fontSize: '0.75rem'
                }}
              >
                -{prod.descuento}% 
              </Box>
            )}
            <Box component="img"
              src={prod.imagen}
              alt={prod.nombre}
              sx={{
                width: '100%',
                borderRadius: 2,
                objectFit: 'cover'
              }}
            />
            {/* Precio */}
            <Box sx={{ mt: 1, display: 'flex', alignItems: 'baseline', gap: 1 }}>
              <Typography sx={{ color: '#d32f2f', fontWeight: 'bold', fontSize: '1.1rem' }}>
                ${prod.precio.toFixed(2)}
              </Typography>
              <Typography sx={{ color: '#999', textDecoration: 'line-through', fontSize: '0.85rem' }}>
                {prod.precioOriginal && `$${prod.precioOriginal.toFixed(2)}`}
              </Typography>
            </Box>
            <Typography variant="caption" sx={{ color: '#666', display: 'block', mb: 1 }}>
              (Exento de IVA)
            </Typography>
            {/* Nombre enlace */}
            <Link
              href={`/producto/${prod.id}`}
              sx={{
                display: 'block',
                fontWeight: 'bold',
                color: '#388e3c',
                textDecoration: 'underline',
                mb: 1,
                fontSize: '0.875rem'
              }}
            >
              {prod.nombre}
            </Link>
            {/* Botón Agregar */}
            <Button
              fullWidth
              onClick={() => handleCompra(prod.id)}
              sx={{
                border: '1px solid #fb8c00',
                color: '#fb8c00',
                textTransform: 'none',
                borderRadius: 2,
                py: 1
              }}
            >
              Agregar
            </Button>
          </Paper>
        </Grid>
      ))}
    </Grid>
  );

  return (
    <Box sx={{ pt: 12, pb: 6, px: { xs: 2, md: 4 } }}>
      {/* Header e imagen grande ya incluidos anteriormente */}
      
      {/* Filtro de categorías */}
      <Box sx={{ mb: 4, display: 'flex', gap: 3, alignItems: 'center' }}>
        {categorias.map(cat => (
          <Button
            key={cat}
            onClick={() => setFiltro(cat)}
            variant={filtro === cat ? 'contained' : 'text'}
            sx={{
              textTransform: 'none',
              borderRadius: 2,
              border: filtro === cat ? '1px solid #388e3c' : '1px solid transparent',
              bgcolor: filtro === cat ? '#e8f5e9' : 'transparent',
              color: filtro === cat ? '#388e3c' : '#444',
              px: 2
            }}
          >
            {cat}
          </Button>
        ))}
      </Box>

      {/* Sección Destacados */}
      <Stack direction="row" alignItems="center" justifyContent="space-between" sx={{ mb: 2 }}>
        <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
          PRODUCTOS <Box component="span" sx={{ color: '#388e3c' }}>DESTACADOS</Box>
        </Typography>
        <Stack direction="row" spacing={1}>
          <IconButton size="small"><ArrowBackIosNewIcon fontSize="inherit" /></IconButton>
          <IconButton size="small"><ArrowForwardIosIcon fontSize="inherit" /></IconButton>
        </Stack>
      </Stack>
      {renderCarrusel(destacados)}

      {/* Más vendidos */}
      <Stack direction="row" alignItems="center" justifyContent="space-between" sx={{ mt: 6, mb: 2 }}>
        <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#388e3c' }}>
          MÁS VENDIDOS
        </Typography>
        <Stack direction="row" spacing={1}>
          <IconButton size="small"><ArrowBackIosNewIcon fontSize="inherit" /></IconButton>
          <IconButton size="small"><ArrowForwardIosIcon fontSize="inherit" /></IconButton>
        </Stack>
      </Stack>
      {renderCarrusel(masVendidos)}

      {/* Últimos vendidos */}
      <Stack direction="row" alignItems="center" justifyContent="space-between" sx={{ mt: 6, mb: 2 }}>
        <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#388e3c' }}>
          ÚLTIMOS VENDIDOS
        </Typography>
        <Stack direction="row" spacing={1}>
          <IconButton size="small"><ArrowBackIosNewIcon fontSize="inherit" /></IconButton>
          <IconButton size="small"><ArrowForwardIosIcon fontSize="inherit" /></IconButton>
        </Stack>
      </Stack>
      {renderCarrusel(ultimos)}
    </Box>
  );
}
