import React, { useContext } from 'react';
import { AccessibilityContext } from '../../context/AccessibilityContext';
import { Box, Button, Typography } from '@mui/material';

export default function AccessibilityControls() {
  const {
    altoContraste,
    modoDislexia,
    tamañoTexto,
    toggleContraste,
    toggleDislexia,
    aumentarTexto,
    disminuirTexto
  } = useContext(AccessibilityContext);

  return (
    <Box sx={{
      mt: 4,
      p: 2,
      border: '1px solid #ccc',
      borderRadius: '8px',
      backgroundColor: altoContraste ? '#222' : '#f0f0f0',
      color: altoContraste ? '#fff' : '#000',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center'
    }}>
      <Typography variant="h6" gutterBottom>
        ⚙️ Controles de Accesibilidad
      </Typography>

      <Button
        variant="contained"
        color={altoContraste ? 'secondary' : 'primary'}
        onClick={toggleContraste}
        sx={{ mb: 1 }}
      >
        {altoContraste ? 'Desactivar Contraste' : 'Activar Contraste'}
      </Button>

      <Button
        variant="contained"
        color={modoDislexia ? 'secondary' : 'primary'}
        onClick={toggleDislexia}
        sx={{ mb: 1 }}
      >
        {modoDislexia ? 'Desactivar modo Dislexia' : 'Activar modo Dislexia'}
      </Button>

      <Typography variant="body2">
        Tamaño de texto actual: <strong>{tamañoTexto}px</strong>
      </Typography>

      <Box sx={{ mt: 1 }}>
        <Button onClick={aumentarTexto} variant="outlined" sx={{ mr: 1 }}>
          Aumentar texto
        </Button>
        <Button onClick={disminuirTexto} variant="outlined">
          Disminuir texto
        </Button>
      </Box>
    </Box>
  );
}
